import React, { useState } from "react";
import "./Doctordashboard.css";
import { Pie, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  BarElement,
} from "chart.js";

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const Doctordashboard = () => {
  const [activeTab, setActiveTab] = useState(""); // Toggle between tabs
  const [profileData, setProfileData] = useState(null);
  const [availabilityList, setAvailabilityList] = useState([]);
  const [appointments, setAppointments] = useState([
    { id: 1, status: "Scheduled" },
    { id: 2, status: "Completed" },
    { id: 3, status: "Canceled" },
    { id: 4, status: "Scheduled" },
  ]); // Sample appointment data

  const appointmentStatuses = appointments.reduce(
    (acc, appointment) => {
      acc[appointment.status]++;
      return acc;
    },
    { Scheduled: 0, Completed: 0, Canceled: 0 }
  );

  // Data for the pie chart
  const pieData = {
    labels: ["Scheduled", "Completed", "Canceled"],
    datasets: [
      {
        data: [
          appointmentStatuses.Scheduled,
          appointmentStatuses.Completed,
          appointmentStatuses.Canceled,
        ],
        backgroundColor: ["#4caf50", "#2196f3", "#f44336"],
      },
    ],
  };

  // Data for bar chart
  const barData = {
    labels: ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"],
    datasets: [
      {
        label: "Appointments Scheduled",
        data: [2, 5, 3, 4, 6, 7, 1], // Sample data for appointments per day
        backgroundColor: "#4caf50",
      },
    ],
  };

  const handleLogout = () => {
    alert("Logged out successfully!");
    window.location.href = "/login"; // Redirect to login page
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
        <button onClick={() => setActiveTab("dashboard")}>Dashboard</button>
        {profileData ? (
          <button onClick={() => setActiveTab("doctorProfile")}>Doctor Profile</button>
        ) : (
          <button onClick={() => setActiveTab("createProfile")}>Create Profile</button>
        )}
        {availabilityList.length > 0 ? (
          <button onClick={() => setActiveTab("viewAvailability")}>View Availability</button>
        ) : (
          <button onClick={() => setActiveTab("addAvailability")}>Add Availability</button>
        )}
        <button onClick={() => setActiveTab("viewAppointments")}>
          View Appointments
        </button>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <h1 className="dashboard-heading">Welcome to Doctor Dashboard</h1>

        {/* Dashboard Overview */}
        {activeTab === "dashboard" && (
          <div className="dashboard-overview">
            <div className="stat-box">
              <h3>Total Appointments</h3>
              <p>{appointments.length}</p>
            </div>
            <div className="stat-box">
              <h3>Total Patients</h3>
              <p>{appointments.length}</p> {/* Placeholder */}
            </div>
            <div className="stat-box">
              <h3>Scheduled Appointments</h3>
              <p>{appointmentStatuses.Scheduled}</p>
            </div>

            {/* Charts */}
            <div className="charts-container">
              <div className="chart">
                <h3>Appointment Status</h3>
                <Pie data={pieData} />
              </div>
              <div className="chart">
                <h3>Availability (Weekly)</h3>
                <Bar data={barData} />
              </div>
            </div>
          </div>
        )}

        {/* Other components like Create Profile, Availability, etc., go here */}
      </div>
    </div>
  );
};

export default Doctordashboard;
