import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const navigate = useNavigate();

  // Hardcoded admin credentials
  const adminCredentials = {
    username: 'admin',
    password: 'admin123',
  };

  // Handle forgot password logic
  const handleForgotPassword = () => {
    if (email) {
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (emailRegex.test(email)) {
        alert(`Password reset link has been sent to ${email}`);
        setShowForgotPassword(false);
      } else {
        alert('Please enter a valid email address.');
      }
    } else {
      alert('Please enter your email address.');
    }
  };

  const handleLogin = (e) => {
    e.preventDefault();

    // Check for admin login
    if (username === adminCredentials.username && password === adminCredentials.password) {
      alert('Admin login successful!');
      localStorage.setItem(
        'loggedInUser',
        JSON.stringify({ username: adminCredentials.username, role: 'Admin' })
      );
      navigate('/dashboard');
      return;
    }

    // Retrieve user data from localStorage
    const storedUsers = JSON.parse(localStorage.getItem('users')) || [];
    const loggedInUser = storedUsers.find(
      (user) => user.username === username && user.password === password
    );

    if (loggedInUser) {
      alert('Login successful!');
      // Store only the logged-in user's information in localStorage
      localStorage.setItem(
        'loggedInUser',
        JSON.stringify({ username: loggedInUser.username, role: 'User' })
      );
      navigate('/dashboard');
    } else {
      alert('Invalid username or password!');
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>Login</h2>
        <label htmlFor="username">Username</label>
        <input
          type="text"
          id="username"
          name="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
        <p>New user? <a href="/register">Register here</a></p>

        {/* Forgot Password Button */}
        <button
          type="button"
          onClick={() => setShowForgotPassword(true)}
          className="forgot-password-button"
        >
          Forgot Password?
        </button>
      </form>

      {/* Forgot Password Form */}
      {showForgotPassword && (
        <div className="forgot-password-form">
          <h3>Enter your email to reset password</h3>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <button type="button" onClick={handleForgotPassword}>
            Send Reset Link
          </button>
          <button type="button" onClick={() => setShowForgotPassword(false)}>
            Cancel
          </button>
        </div>
      )}
    </div>
  );
}

export default Login;
