import React, { useState } from 'react';
import './Chatbot.css';

function Chatbot() {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    { sender: 'bot', text: 'Hi! How can I assist you today? Please select a category below.' },
  ]);

  // Categories and Questions
  const categories = {
    Login: [
      { question: 'How do I log in?', answer: 'Use your registered email and password on the login page to access your account.' },
      { question: 'I’m unable to log in. What should I do?', answer: 'Ensure your email and password are correct. Use "Forgot Password" if needed.' },
    ],
    Register: [
      { question: 'How do I register?', answer: 'Visit the registration page and fill in the required details to create an account.' },
      { question: 'Why do I need to register?', answer: 'Registration allows you to book appointments and access personalized services.' },
    ],
    Password: [
      { question: 'How do I reset my password?', answer: 'Click the "Forgot Password" link on the login page and follow the instructions.' },
      { question: 'Can I change my password?', answer: 'Yes, go to your account settings and choose "Change Password".' },
    ],
    Appointment: [
      { question: 'How do I book an appointment?', answer: 'Log in, select a doctor, and choose an available time slot to book your appointment.' },
      { question: 'Can I reschedule my appointment?', answer: 'Yes, log in to your account, go to "Appointments", and select the reschedule option.' },
    ],
  };

  const toggleChatbot = () => setIsOpen(!isOpen);

  const handleQuestionClick = (qa) => {
    setMessages((prev) => [
      ...prev,
      { sender: 'user', text: qa.question },
      { sender: 'bot', text: qa.answer },
    ]);
  };

  return (
    <>
      {/* Chatbot Trigger */}
      <div className="chatbot-trigger">
        <button className="open-chatbot-btn" onClick={toggleChatbot}>
          <i className="fas fa-comments"></i>
        </button>
        <p className="chatbot-label">Chat with Us</p>
      </div>

      {/* Chatbot Container */}
      {isOpen && (
        <div className="chatbot-container">
          <div className="chatbot-header">
            Chat with Us
            <span onClick={toggleChatbot}>X</span>
          </div>
          <div className="chatbot-body">
            {messages.map((msg, i) => (
              <div key={i} className={`chatbot-message ${msg.sender === 'bot' ? 'bot-message' : 'user-message'}`}>
                {msg.text}
              </div>
            ))}
          </div>
          <div className="chatbot-footer">
            {Object.keys(categories).map((category, idx) => (
              <div key={idx} className="chatbot-category">
                <h4>{category} Questions</h4>
                {categories[category].map((qa, i) => (
                  <button
                    key={i}
                    className="chatbot-question-btn"
                    onClick={() => handleQuestionClick(qa)}
                  >
                    {qa.question}
                  </button>
                ))}
              </div>
            ))}
          </div>
        </div>
      )}
    </>
  );
}

export default Chatbot;
