import React, { useState } from 'react';
import './DoctorProfile.css';
function DoctorProfile() {
  // State to store form data
  const [formData, setFormData] = useState({
    name: '',
    specialty: '',
    contact: '',
    email: '',
    experience: '',
  });

  // Handler to manage form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Handler for form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Profile saved successfully!');
    // Optionally: You can add logic to save the data to a backend here
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <form onSubmit={handleSubmit} style={{ width: '400px', padding: '20px', border: '1px solid #ccc', borderRadius: '10px', backgroundColor: '#f9f9f9' }}>
        <h2>Create Profile</h2>

        <label>
          Name:
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </label>

        <label>
          Specialty:
          <input type="text" name="specialty" value={formData.specialty} onChange={handleChange} required />
        </label>

        <label>
          Contact:
          <input type="text" name="contact" value={formData.contact} onChange={handleChange} required />
        </label>

        <label>
          Email:
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </label>

        <label>
          Experience (Years):
          <input type="text" name="experience" value={formData.experience} onChange={handleChange} required />
        </label>

        <button type="submit" style={{ marginTop: '10px', padding: '10px', width: '100%' }}>
          Save Profile
        </button>
      </form>
    </div>
  );
}

export default DoctorProfile;
