import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  // Hardcoded admin credentials
  const adminCredentials = {
    username: 'admin', // Admin username
    password: 'admin123', // Admin password
  };

  // Handle forgot password logic (for now, just logging the action)
  const handleForgotPassword = () => {
    alert('Forgot password functionality not implemented yet.');
    // You can replace this with actual logic for sending a reset link or emailing the user
  };

  const handleLogin = (e) => {
    e.preventDefault();

    // Check for admin login
    if (username === adminCredentials.username && password === adminCredentials.password) {
      // Admin login successful, navigate to dashboard
      alert('Admin login successful!');
      navigate('/dashboard');  // Redirect to the admin dashboard
      return; // Exit the function here to prevent further checks for other users
    }

    // Retrieve the stored user data from localStorage for normal users
    const storedUser = JSON.parse(localStorage.getItem('user'));

    // Validate the login credentials for normal users
    if (storedUser && storedUser.username === username && storedUser.password === password) {
      alert('Login successful!');
      navigate('/dashboard');  // Redirect to the dashboard
    } else {
      alert('Invalid username or password!');
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>Login</h2>
        <label htmlFor="username">Username</label>
        <input
          type="text"
          id="username"
          name="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
        <p>New user? <a href="/register">Register here</a></p>

        {/* Forgot Password button */}
        <button type="button" onClick={handleForgotPassword} className="forgot-password-button">
          Forgot Password?
        </button>
      </form>
    </div>
  );
}

export default Login;
