import React, { useState } from "react";
import "./Doctordashboard.css";

const Doctordashboard = () => {
  const [activeTab, setActiveTab] = useState(""); // To toggle between tabs
  const [profileData, setProfileData] = useState(null); // Store doctor profile
  const [availabilityList, setAvailabilityList] = useState([]); // Store doctor's availability
  const [profileFormData, setProfileFormData] = useState({
    name: "",
    DoctorID: "",
    specialization: "",
    experience: "",
    Location:"",
    Hospitalname:"",
    Email:"",
    MoblieNo:"",
    profilePhoto: null,
  });

  const [availabilityFormData, setAvailabilityFormData] = useState({
    doctorID: "",
    availabilityID: "",
    startDate: "",
    endDate: "",
    Time:"",
    index: null, // Track the index of the item to edit
  });

  const handleLogout = () => {
    alert("Logged out successfully!");
    window.location.href = "/login"; // Redirect to login page
  };

  const handleProfileFormChange = (e) => {
    const { name, value } = e.target;
    setProfileFormData({ ...profileFormData, [name]: value });
  };

  const handleAvailabilityFormChange = (e) => {
    const { name, value } = e.target;
    setAvailabilityFormData({ ...availabilityFormData, [name]: value });
  };

  const handleProfileSubmit = (e) => {
    e.preventDefault();
    setProfileData(profileFormData); // Save profile data
    setProfileFormData({ name: "", DoctorID:"", specialization: "", experience: "",Location:"",Hospitalname:"",Email:"",MoblieNO:"", profilePhoto: null });
    setActiveTab("doctorProfile"); // After saving, show the profile info
    alert("Profile created successfully!");
  };

  const handleAddAvailabilitySubmit = (e) => {
    e.preventDefault();
    if (availabilityFormData.index !== null) {
      // Edit existing availability
      const updatedList = [...availabilityList];
      updatedList[availabilityFormData.index] = availabilityFormData;
      setAvailabilityList(updatedList);
      setAvailabilityFormData({
        doctorID: "",
        availabilityID: "",
        startDate: "",
        endDate: "",
        Time: "",
        index: null,
      });
    } else {
      // Add new availability
      setAvailabilityList([...availabilityList, availabilityFormData]);
    }
    setActiveTab(""); // Reset view
    alert("Availability updated successfully!");
  };

  const handleEditProfile = () => {
    setProfileFormData(profileData); // Load existing profile data into the form
    setActiveTab("createProfile"); // Open the profile creation form to edit
  };

  const handleDeleteProfile = () => {
    if (window.confirm("Are you sure you want to delete your profile?")) {
      setProfileData(null);
      alert("Profile deleted successfully!");
    }
  };

  const handleEditAvailability = (index) => {
    setAvailabilityFormData({
      ...availabilityList[index],
      index: index, // Track the index of the availability to be edited
    });
    setActiveTab("addAvailability"); // Open the availability form to edit
  };

  const handleDeleteAvailability = (index) => {
    if (window.confirm("Are you sure you want to delete this availability?")) {
      const updatedList = availabilityList.filter((_, i) => i !== index);
      setAvailabilityList(updatedList);
      alert("Availability deleted successfully!");
    }
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
      <button onClick={() => setActiveTab("Dashboard")}>
          Dashboard
        </button>
        {profileData ? (
          
          <button onClick={() => setActiveTab("doctorProfile")}>Doctor Profile</button>
        ) : (
          <button onClick={() => setActiveTab("createProfile")}>Create Profile</button>
        )}
        {availabilityList.length > 0 ? (
          <button onClick={() => setActiveTab("viewAvailability")}>View Availability</button>
        ) : (
          <button onClick={() => setActiveTab("addAvailability")}>Add Availability</button>
        )}
        <button onClick={() => setActiveTab("viewAppointments")}>
          View Appointments
        </button>
        
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Top Heading */}
        <h1 className="dashboard-heading">Welcome to Doctor Dashboard</h1>

        {/* Create Profile Form or Profile Info */}
        {activeTab === "createProfile" && (
          <div className="centered-form">
            <form onSubmit={handleProfileSubmit}>
              <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
                {profileData ? "Edit Doctor Profile" : "Create Doctor Profile"}
              </h2>
              <input
                type="text"
                name="name"
                placeholder="Name"
                value={profileFormData.name}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="DoctorID"
                placeholder="DoctorID"
                value={profileFormData.DoctorID}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="specialization"
                placeholder="Specialization"
                value={profileFormData.specialization}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="experience"
                placeholder="Experience (in years)"
                value={profileFormData.experience}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="Location"
                placeholder="Location"
                value={profileFormData.Location}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="Hospitalname"
                placeholder="Hospitalname"
                value={profileFormData.Hospitalname}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="Email"
                placeholder="Email"
                value={profileFormData.Email}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="MoblieNo"
                placeholder="MoblieNo"
                value={profileFormData.MoblieNO}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="file"
                name="profilePhoto"
                onChange={(e) =>
                  setProfileFormData({ ...profileFormData, profilePhoto: e.target.files[0] })
                }
              />
              <button type="submit">Submit</button>
            </form>
          </div>
        )}

        {/* Doctor Profile Info */}
        {activeTab === "doctorProfile" && profileData && (
          <div className="centered-form">
            <h2>Doctor Profile</h2>
            <p><strong>Name:</strong> {profileData.name}</p>
            <p><strong>DoctorID:</strong> {profileData.DoctorID}</p>
            <p><strong>Specialization:</strong> {profileData.specialization}</p>
            <p><strong>Experience:</strong> {profileData.experience} years</p>
            <p><strong>Location:</strong> {profileData.Location}</p>
            <p><strong>Hospital name:</strong> {profileData.Hospitalname}</p>
            <p><strong>Email:</strong> {profileData.Email}</p>
            <p><strong>MoblieNo:</strong> {profileData.MoblieNo}</p>
            {profileData.profilePhoto && (
              <img
                src={URL.createObjectURL(profileData.profilePhoto)}
                alt="Profile"
                style={{ width: "150px", borderRadius: "50%" }}
              />
            )}
            <div className="profile-actions">
              <button onClick={handleEditProfile}>Edit Profile</button>
              <button onClick={handleDeleteProfile} style={{ background: "red", color: "white" }}>
                Delete Profile
              </button>
            </div>
          </div>
        )}

        {/* Add/Edit Availability Form or Availability Info */}
        {activeTab === "addAvailability" && (
          <div className="centered-form">
            <form onSubmit={handleAddAvailabilitySubmit}>
              <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
                {availabilityFormData.index !== null ? "Edit Availability" : "Add Availability"}
              </h2>
              <input
                type="text"
                name="doctorID"
                placeholder="Doctor ID"
                value={availabilityFormData.doctorID}
                onChange={handleAvailabilityFormChange}
                required
              />
              <input
                type="text"
                name="availabilityID"
                placeholder="availabilityID"
                value={availabilityFormData.availabilityID}
                onChange={handleAvailabilityFormChange}
                required
              />
              <input
                type="date"
                name="startDate"
                value={availabilityFormData.startDate}
                onChange={handleAvailabilityFormChange}
                required
              />
              <input
                type="date"
                name="endDate"
                value={availabilityFormData.endDate}
                onChange={handleAvailabilityFormChange}
                required
              />
              <input
                type="text"
                name="Time"
                placeholder="Time"
                value={availabilityFormData.Time}
                onChange={handleAvailabilityFormChange}
                required
              />
              <button type="submit">Submit</button>
            </form>
          </div>
        )}

        {/* View Availability List */}
        {activeTab === "viewAvailability" && availabilityList.length > 0 && (
          <div className="availability-list">
            <h2>Doctor Availability</h2>
            {availabilityList.map((availability, index) => (
              <div key={index} className="availability-item">
                <p><strong>Doctor ID:</strong> {availability.doctorID}</p>
                <p><strong>Availability ID:</strong> {availability.availabilityID}</p>
                <p><strong>Start Date:</strong> {availability.startDate}</p>
                <p><strong>End Date:</strong> {availability.endDate}</p>
                <p><strong>Time:</strong> {availability.Time}</p>
                <div className="availability-actions">
                  <button onClick={() => handleEditAvailability(index)}>Edit</button>
                  <button onClick={() => handleDeleteAvailability(index)} style={{ background: "red", color: "white" }}>
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};
<div className="appointment-container">
      <h2>Appointments</h2>

      {/* Add Button */}
      <div className="add-button-container">
        <button onClick={() => setShowForm(!showForm)} className="add-button">
          {showForm ? 'Cancel' : 'Add Appointment'}
        </button>
      </div>

      {/* Appointment List Table */}
      <div className="appointment-table">
        <h3>List of Appointments</h3>
        <table>
          <thead>
            <tr>
              <th>Appointment ID</th>
              <th>Patient ID</th>
              <th>Doctor ID</th>
              <th>Appointment Date</th>
              <th>Appointment Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {appointments.length > 0 ? (
              appointments.map((appointment, index) => (
                <tr key={index}>
                  <td>{appointment.appointmentId}</td>
                  <td>{appointment.patientId}</td>
                  <td>{appointment.doctorId}</td>
                  <td>{appointment.appointmentDate}</td>
                  <td>{appointment.appointmentStatus}</td>
                  <td>
                    <button>Edit</button>
                    <button>Delete</button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6" style={{ textAlign: 'center', color: '#888' }}>
                  No appointments added yet.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Form for Adding New Appointment */}
      {showForm && (
        <div className="add-appointment-form">
          <h3>Add Appointment</h3>
          <form onSubmit={handleSubmit}>
            <label>
              Appointment ID:
              <input
                type="text"
                name="appointmentId"
                value={newAppointment.appointmentId}
                onChange={handleChange}
                required
              />
            </label>
            <label>
              Patient ID:
              <input
                type="text"
                name="patientId"
                value={newAppointment.patientId}
                onChange={handleChange}
                required
              />
            </label>
            <label>
              Doctor ID:
              <input
                type="text"
                name="doctorId"
                value={newAppointment.doctorId}
                onChange={handleChange}
                required
              />
            </label>
            <label>
              Appointment Date:
              <input
                type="date"
                name="appointmentDate"
                value={newAppointment.appointmentDate}
                onChange={handleChange}
                required
              />
            </label>
            <label>
              Appointment Status:
              <select
                name="appointmentStatus"
                value={newAppointment.appointmentStatus}
                onChange={handleChange}
                required
              >
                <option value="">Select Status</option>
                <option value="Scheduled">Scheduled</option>
                <option value="Completed">Completed</option>
                <option value="Cancelled">Cancelled</option>
              </select>
            </label>
            <button type="submit">Add Appointment</button>
          </form>
        </div>
      )}
    </div>

export default Doctordashboard;
