import React, { useState } from "react";

const Doctordashboard = () => {
  const [profileData, setProfileData] = useState({
    name: "",
    DoctorID: "",
    specialization: "",
    experience: "",
    Location: "",
    Hospitalname: "",
    Email: "",
    MobileNo: "",
  });

  const handleChange = (e) => {
    setProfileData({ ...profileData, [e.target.name]: e.target.value });
  };

  const handleProfileSubmit = () => {
    const existingProfiles = JSON.parse(localStorage.getItem("doctorProfiles")) || [];
    existingProfiles.push(profileData);
    localStorage.setItem("doctorProfiles", JSON.stringify(existingProfiles));
    alert("Profile saved successfully!");
  };

  return (
    <div>
      <h1>Create Doctor Profile</h1>
      <form onSubmit={(e) => e.preventDefault()}>
        <input name="name" placeholder="Name" onChange={handleChange} />
        <input name="DoctorID" placeholder="Doctor ID" onChange={handleChange} />
        <input name="specialization" placeholder="Specialization" onChange={handleChange} />
        <input name="experience" placeholder="Experience (years)" onChange={handleChange} />
        <input name="Location" placeholder="Location" onChange={handleChange} />
        <input name="Hospitalname" placeholder="Hospital Name" onChange={handleChange} />
        <input name="Email" placeholder="Email" onChange={handleChange} />
        <input name="MobileNo" placeholder="Mobile No" onChange={handleChange} />
        <button type="submit" onClick={handleProfileSubmit}>
          Submit
        </button>
      </form>
    </div>
  );
};

export default Doctordashboard;
