import React, { useState } from 'react';

const CreateDoctor = ({ addDoctor }) => {
  const [doctor, setDoctor] = useState({
    name: '',
    id: '',
    specialty: '',
    location: '',
    hospitalName: '',
    email: '',
    phoneNumber: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setDoctor((prevDoctor) => ({
      ...prevDoctor,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addDoctor(doctor); // Pass the doctor object to the parent component
    setDoctor({ name: '', id: '', specialty: '', location: '', hospitalName: '', email: '', phoneNumber: '' });
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input type="text" name="name" value={doctor.name} onChange={handleChange} required />
      </label>
      <label>
        ID:
        <input type="text" name="id" value={doctor.id} onChange={handleChange} required />
      </label>
      <label>
        Specialty:
        <input type="text" name="specialty" value={doctor.specialty} onChange={handleChange} required />
      </label>
      <label>
        Location:
        <input type="text" name="location" value={doctor.location} onChange={handleChange} required />
      </label>
      <label>
        Hospital Name:
        <input type="text" name="hospitalName" value={doctor.hospitalName} onChange={handleChange} required />
      </label>
      <label>
        Email:
        <input type="email" name="email" value={doctor.email} onChange={handleChange} required />
      </label>
      <label>
        Phone Number:
        <input type="text" name="phoneNumber" value={doctor.phoneNumber} onChange={handleChange} required />
      </label>
      <button type="submit">Add Doctor</button>
    </form>
  );
};

export default CreateDoctor;
