import React, { useState } from "react";
import "./Doctordashboard.css";

const Doctordashboard = () => {
  const [activeTab, setActiveTab] = useState(""); // To toggle between tabs
  const [profileData, setProfileData] = useState(null); // Store doctor profile
  const [availabilityData, setAvailabilityData] = useState(null); // Store availability data
  const [isEditingProfile, setIsEditingProfile] = useState(false); // Toggle profile edit form
  const [isEditingAvailability, setIsEditingAvailability] = useState(false); // Toggle availability edit form

  const [profileFormData, setProfileFormData] = useState({
    name: "",
    specialization: "",
    experience: "",
    profilePhoto: null,
  });

  const [availabilityFormData, setAvailabilityFormData] = useState({
    doctorID: "",
    startDate: "",
    endDate: "",
  });

  const handleLogout = () => {
    alert("Logged out successfully!");
    window.location.href = "/login"; // Redirect to login page
  };

  const handleProfileFormChange = (e) => {
    const { name, value } = e.target;
    setProfileFormData({ ...profileFormData, [name]: value });
  };

  const handleProfilePhotoChange = (e) => {
    setProfileFormData({
      ...profileFormData,
      profilePhoto: e.target.files[0],
    });
  };

  const handleAvailabilityFormChange = (e) => {
    const { name, value } = e.target;
    setAvailabilityFormData({ ...availabilityFormData, [name]: value });
  };

  const handleProfileSubmit = (e) => {
    e.preventDefault();
    setProfileData(profileFormData); // Save profile data
    setIsEditingProfile(false); // Exit edit mode
    setActiveTab(""); // Reset view
    alert("Profile updated successfully!");
  };

  const handleAvailabilitySubmit = (e) => {
    e.preventDefault();
    setAvailabilityData(availabilityFormData); // Save availability data
    setIsEditingAvailability(false); // Exit edit mode
    setActiveTab(""); // Reset view
    alert("Availability updated successfully!");
  };

  const handleEditProfile = () => {
    setProfileFormData(profileData); // Load existing profile data
    setIsEditingProfile(true); // Enter edit mode
    setActiveTab("createProfile");
  };

  const handleDeleteProfile = () => {
    if (window.confirm("Are you sure you want to delete your profile?")) {
      setProfileData(null);
      alert("Profile deleted successfully!");
    }
  };

  const handleEditAvailability = () => {
    setAvailabilityFormData(availabilityData); // Load existing availability data
    setIsEditingAvailability(true); // Enter edit mode
    setActiveTab("addAvailability");
  };

  const handleDeleteAvailability = () => {
    if (window.confirm("Are you sure you want to delete your availability?")) {
      setAvailabilityData(null);
      alert("Availability deleted successfully!");
    }
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
        <button onClick={() => setActiveTab("createProfile")}>
          Create Profile
        </button>
        <button onClick={() => setActiveTab("addAvailability")}>
          Add Availability
        </button>
        <button onClick={() => setActiveTab("viewAppointments")}>
          View Appointments
        </button>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Display Doctor Profile */}
        {activeTab === "createProfile" && profileData && !isEditingProfile ? (
          <div className="profile-section">
            <h2>Doctor Profile</h2>
            <p><strong>Name:</strong> {profileData.name}</p>
            <p><strong>Specialization:</strong> {profileData.specialization}</p>
            <p><strong>Experience:</strong> {profileData.experience} years</p>
            {profileData.profilePhoto && (
              <img
                src={URL.createObjectURL(profileData.profilePhoto)}
                alt="Profile"
                style={{ width: "150px", borderRadius: "50%" }}
              />
            )}
            <div className="profile-actions">
              <button onClick={handleEditProfile}>Edit Profile</button>
              <button
                onClick={handleDeleteProfile}
                style={{ background: "red", color: "white" }}
              >
                Delete Profile
              </button>
            </div>
          </div>
        ) : activeTab === "createProfile" && (
          <div className="centered-form">
            <form onSubmit={handleProfileSubmit}>
              <h2>{isEditingProfile ? "Edit Doctor Profile" : "Create Doctor Profile"}</h2>
              <input
                type="text"
                name="name"
                placeholder="Name"
                value={profileFormData.name}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="specialization"
                placeholder="Specialization"
                value={profileFormData.specialization}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="experience"
                placeholder="Experience (in years)"
                value={profileFormData.experience}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="file"
                name="profilePhoto"
                accept="image/*"
                onChange={handleProfilePhotoChange}
              />
              <button type="submit">{isEditingProfile ? "Update Profile" : "Submit"}</button>
            </form>
          </div>
        )}

        {/* Add/Edit Availability Section */}
        {activeTab === "addAvailability" && availabilityData && !isEditingAvailability ? (
          <div className="availability-section">
            <h2>Doctor Availability</h2>
            <p><strong>Doctor ID:</strong> {availabilityData.doctorID}</p>
            <p><strong>Start Date:</strong> {availabilityData.startDate}</p>
            <p><strong>End Date:</strong> {availabilityData.endDate}</p>
            <div className="availability-actions">
              <button onClick={handleEditAvailability}>Edit Availability</button>
              <button
                onClick={handleDeleteAvailability}
                style={{ background: "red", color: "white" }}
              >
                Delete Availability
              </button>
            </div>
          </div>
        ) : activeTab === "addAvailability" && (
          <div className="centered-form">
            <form onSubmit={handleAvailabilitySubmit}>
              <h2>{isEditingAvailability ? "Edit Availability" : "Add Availability"}</h2>
              <input
                type="text"
                name="doctorID"
                placeholder="Doctor ID"
                value={availabilityFormData.doctorID}
                onChange={handleAvailabilityFormChange}
                required
              />
              <input
                type="date"
                name="startDate"
                value={availabilityFormData.startDate}
                onChange={handleAvailabilityFormChange}
                required
              />
              <input
                type="date"
                name="endDate"
                value={availabilityFormData.endDate}
                onChange={handleAvailabilityFormChange}
                required
              />
              <button type="submit">{isEditingAvailability ? "Update Availability" : "Submit"}</button>
            </form>
          </div>
        )}

        {/* View Appointments Section */}
        {activeTab === "viewAppointments" && (
          <div className="appointments-section">
            <h2>Appointments</h2>
            <p>Here, doctors can view and manage their appointments.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Doctordashboard;
