// src/components/AppointmentList.js
import React, { useState } from 'react';
import './AppointmentList.css';

function AppointmentList() {
  const [appointments, setAppointments] = useState([]); // State to hold appointments
  const [newAppointment, setNewAppointment] = useState({
    appointmentId: '',
    patientId: '',
    doctorId: '',
    appointmentDate: '',
    appointmentStatus: 'Scheduled',
  });

  // Handle form field changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewAppointment((prevAppointment) => ({
      ...prevAppointment,
      [name]: value,
    }));
  };

  // Handle form submission (adding new appointment)
  const handleSubmit = (e) => {
    e.preventDefault();
    setAppointments((prevAppointments) => [...prevAppointments, newAppointment]);
    setNewAppointment({
      appointmentId: '',
      patientId: '',
      doctorId: '',
      appointmentDate: '',
      appointmentStatus: 'Scheduled',
    });
  };

  return (
    <div className="appointment-list">
      <h2>Appointment List</h2>

      {/* Add Appointment Form */}
      <div className="add-appointment">
        <h3>Add New Appointment</h3>
        <form onSubmit={handleSubmit}>
          <label>
            Appointment ID:
            <input
              type="text"
              name="appointmentId"
              value={newAppointment.appointmentId}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Patient ID:
            <input
              type="text"
              name="patientId"
              value={newAppointment.patientId}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Doctor ID:
            <input
              type="text"
              name="doctorId"
              value={newAppointment.doctorId}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Appointment Date:
            <input
              type="date"
              name="appointmentDate"
              value={newAppointment.appointmentDate}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Appointment Status:
            <select
              name="appointmentStatus"
              value={newAppointment.appointmentStatus}
              onChange={handleChange}
            >
              <option value="Scheduled">Scheduled</option>
              <option value="Completed">Completed</option>
              <option value="Cancelled">Cancelled</option>
            </select>
          </label>
          <button type="submit">Add Appointment</button>
        </form>
      </div>

      {/* Appointment List Table */}
      <div className="appointment-table">
        <h3>List of Appointments</h3>
        <table>
          <thead>
            <tr>
              <th>Appointment ID</th>
              <th>Patient ID</th>
              <th>Doctor ID</th>
              <th>Appointment Date</th>
              <th>Appointment Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {appointments.length === 0 ? (
              <tr>
                <td colSpan="6">No appointments available</td>
              </tr>
            ) : (
              appointments.map((appointment, index) => (
                <tr key={index}>
                  <td>{appointment.appointmentId}</td>
                  <td>{appointment.patientId}</td>
                  <td>{appointment.doctorId}</td>
                  <td>{appointment.appointmentDate}</td>
                  <td>{appointment.appointmentStatus}</td>
                  <td>
                    <button>Edit</button>
                    <button>Delete</button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AppointmentList;
