import React, { useState } from 'react';
import './Home.css';

function Home() {
  const [showOpeningHours, setShowOpeningHours] = useState(false);

  const toggleOpeningHours = () => {
    setShowOpeningHours(!showOpeningHours);
  };

  return (
    <div className="home-container">
      <div className="home-overlay"></div> {/* Overlay for readability */}
      <div className="home-content" style={{ textAlign: 'left', marginLeft: '-550px' }}>
        <h1>Experienced quality medical services to rely on!</h1>
        <h2>Your health is our priority.</h2>
        <p>
          At DoctorApp, we bring you exceptional healthcare services tailored to meet your unique needs. 
          With an experienced team of professionals and state-of-the-art facilities, you can trust us to be with you 
          every step of your healthcare journey.
        </p>
        {/* Button to toggle Opening Hours */}
        <button className="toggle-button" onClick={toggleOpeningHours}>
          {showOpeningHours ? 'Hide Opening Hours' : 'Show Opening Hours'}
        </button>
      </div>

      {/* Conditionally Render Opening Hours */}
      {showOpeningHours && (
        <div className="opening-hours" style={{ marginTop: '20px', textAlign: 'center', padding: '20px' }}>
          <h3 style={{ fontSize: '24px', marginBottom: '20px' }}>Opening Hours</h3>
          <div className="hours-box">
            <p><span role="img" aria-label="clock">ðŸ•’</span> Monday - Friday: 8:00 AM - 9:00 PM</p>
            <p><span role="img" aria-label="clock">ðŸ•’</span> Saturday: 9:00 AM - 6:00 PM</p>
            <p><span role="img" aria-label="clock">ðŸ•’</span> Sunday: 9:00 AM - 2:00 PM</p>
          </div>
        </div>
      )}

      {/* Footer Section */}
      <footer className="footer1" style={{ marginTop: '50px' }}>
        <div className="footer1-container">
          <p>&copy; 2024 DoctorApp. All rights reserved.</p>
          <p>
            <a href="/terms" style={{ color: '#007BFF', textDecoration: 'none' }}>Terms of Service</a> |{' '}
            <a href="/privacy" style={{ color: '#007BFF', textDecoration: 'none' }}>Privacy Policy</a>
          </p>
        </div>
      </footer>
    </div>
  );
}

export default Home;
