import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();

    // Retrieve the stored user data from localStorage
    const storedUser = JSON.parse(localStorage.getItem('user'));

    // Validate the login credentials
    if (storedUser && storedUser.username === username && storedUser.password === password) {
      console.log("Login successful:", storedUser);
      alert('Login successful!');
      localStorage.setItem('loggedInUser', JSON.stringify(storedUser)); // Store login information
      navigate('/dashboard');  // Redirect to dashboard after successful login
    } else {
      console.log("Login failed. Invalid username or password.");
      alert('Invalid username or password!');
    }
  };
  const handleForgotPassword = () => {
    // Simulate email notification for password reset
    alert('Password reset link has been sent to your email.');
    // Optionally, you can redirect to a password reset page
    // navigate('/reset-password');
  };


  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>Login</h2>
        <label htmlFor="username">Username</label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
        {/* Forgot Password link */}
        <button type="button" onClick={handleForgotPassword} className="forgot-password-button">
          Forgot Password?
        </button>
        <p>New user? <a href="/register">Register here</a></p>
      </form>
    </div>
  );
}

export default Login;
