import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();

    // Dummy login validation (example)
    if (username === 'admin' && password === 'password123') {
      alert('Login successful!');
      navigate('/dashboard');
    } else {
      alert('Invalid username or password!');
    }
  };

  const handleForgotPassword = () => {
    alert("Forgot Password functionality triggered!");
    // Here, you can add code to handle password reset, such as opening a modal or navigating to a reset page
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>Login</h2>
        <label htmlFor="username">Username</label>
        <input
          type="text"
          id="username"
          name="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>

        {/* Forgot Password button */}
        <button
          type="button" // type="button" prevents it from submitting the form
          onClick={handleForgotPassword}
          className="link-button"
        >
          Forgot Password?
        </button>

        <p>New user? <a href="/register">Register here</a></p>
      </form>
    </div>
  );
}

export default Login;
