import React, { useState, useEffect } from 'react';
import './DoctorDashboard.css'; // Assuming you have a separate CSS file for styles

const DoctorDashboard = () => {
  const [profileData, setProfileData] = useState({
    name: "",
    DoctorID: "",
    specialization: "",
    experience: "",
    Location: "",
    Hospitalname: "",
    Email: "",
    MoblieNo: "",
    profilePhoto: null,
  });

  const [profileFormData, setProfileFormData] = useState({
    name: "",
    DoctorID: "",
    specialization: "",
    experience: "",
    Location: "",
    Hospitalname: "",
    Email: "",
    MoblieNo: "",
    profilePhoto: null,
  });

  const [activeTab, setActiveTab] = useState("profileForm");

  const handleProfileSubmit = (e) => {
    e.preventDefault();
    setProfileData(profileFormData); // Save profile data
    localStorage.setItem('doctorProfile', JSON.stringify(profileFormData)); // Store in localStorage
    setProfileFormData({
      name: "",
      DoctorID: "",
      specialization: "",
      experience: "",
      Location: "",
      Hospitalname: "",
      Email: "",
      MoblieNo: "",
      profilePhoto: null,
    });
    setActiveTab("doctorProfile"); // After saving, show the profile info
    alert("Profile created successfully!");
  };

  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setProfileFormData({ ...profileFormData, [name]: value });
  };

  const handleProfileFileChange = (e) => {
    setProfileFormData({ ...profileFormData, profilePhoto: e.target.files[0] });
  };

  // Doctor view profile
  const handleViewProfile = () => {
    const savedProfile = JSON.parse(localStorage.getItem('doctorProfile'));
    setProfileData(savedProfile);
    setActiveTab("doctorProfile"); // Open the profile view
  };

  // Patient view doctor profile
  const [doctorList, setDoctorList] = useState([]);
  useEffect(() => {
    const doctors = JSON.parse(localStorage.getItem('doctorProfiles')) || [];
    setDoctorList(doctors);
  }, []);

  const handleViewDoctorProfile = (doctorID) => {
    const selectedDoctorProfile = doctorList.find(doc => doc.DoctorID === doctorID);
    setActiveTab("doctorProfile"); // Change active tab
    setProfileData(selectedDoctorProfile); // Set the selected doctor's profile data
  };

  return (
    <div className="doctor-dashboard">
      {activeTab === "profileForm" && (
        <form onSubmit={handleProfileSubmit} className="profile-form">
          <h2>Doctor Profile Form</h2>
          <input
            type="text"
            name="name"
            value={profileFormData.name}
            onChange={handleProfileChange}
            placeholder="Doctor Name"
          />
          <input
            type="text"
            name="DoctorID"
            value={profileFormData.DoctorID}
            onChange={handleProfileChange}
            placeholder="Doctor ID"
          />
          <input
            type="text"
            name="specialization"
            value={profileFormData.specialization}
            onChange={handleProfileChange}
            placeholder="Specialization"
          />
          <input
            type="text"
            name="experience"
            value={profileFormData.experience}
            onChange={handleProfileChange}
            placeholder="Experience (years)"
          />
          <input
            type="text"
            name="Location"
            value={profileFormData.Location}
            onChange={handleProfileChange}
            placeholder="Location"
          />
          <input
            type="text"
            name="Hospitalname"
            value={profileFormData.Hospitalname}
            onChange={handleProfileChange}
            placeholder="Hospital Name"
          />
          <input
            type="email"
            name="Email"
            value={profileFormData.Email}
            onChange={handleProfileChange}
            placeholder="Email"
          />
          <input
            type="text"
            name="MoblieNo"
            value={profileFormData.MoblieNo}
            onChange={handleProfileChange}
            placeholder="Mobile No"
          />
          <input
            type="file"
            name="profilePhoto"
            onChange={handleProfileFileChange}
            placeholder="Upload Profile Photo"
          />
          <button type="submit">Save Profile</button>
        </form>
      )}

      {activeTab === "doctorProfile" && profileData && (
        <div className="doctor-profile">
          <h2>Doctor Profile</h2>
          <p><strong>Name:</strong> {profileData.name}</p>
          <p><strong>DoctorID:</strong> {profileData.DoctorID}</p>
          <p><strong>Specialization:</strong> {profileData.specialization}</p>
          <p><strong>Experience:</strong> {profileData.experience} years</p>
          <p><strong>Location:</strong> {profileData.Location}</p>
          <p><strong>Hospital name:</strong> {profileData.Hospitalname}</p>
          <p><strong>Email:</strong> {profileData.Email}</p>
          <p><strong>MoblieNo:</strong> {profileData.MoblieNo}</p>
          {profileData.profilePhoto && (
            <img
              src={URL.createObjectURL(profileData.profilePhoto)}
              alt="Profile"
              style={{ width: "150px", borderRadius: "50%" }}
            />
          )}
        </div>
      )}

      <button onClick={handleViewProfile}>View Profile</button>

      {/* Patient Dashboard Section */}
      <div className="patient-dashboard">
        <h2>Doctor List</h2>
        {doctorList.map(doctor => (
          <div
            key={doctor.DoctorID}
            onClick={() => handleViewDoctorProfile(doctor.DoctorID)}
            className="doctor-item"
          >
            <p>{doctor.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DoctorDashboard;
