import React, { useState } from 'react';
import './CreateDoctor.css';

function CreateDoctor({ addDoctor }) {
  const [doctor, setDoctor] = useState({
    id: '',
    name: '',
    specialty: '',
    location: '',
    hospital: '',
    email: '',
    phone: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setDoctor({
      ...doctor,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (doctor.password === '') {
      alert('Password is required!');
      return;
    }
    addDoctor(doctor); // Adding doctor to the list
    setDoctor({
      id: '',
      name: '',
      specialty: '',
      location: '',
      hospital: '',
      email: '',
      phone: '',
      password: '',
    });
  };

  return (
    <div className="create-doctor-container">
      <h2>Create New Doctor</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Doctor ID:
          <input type="text" name="id" value={doctor.id} onChange={handleChange} required />
        </label>
        <label>
          Name:
          <input type="text" name="name" value={doctor.name} onChange={handleChange} required />
        </label>
        <label>
          Specialty:
          <input type="text" name="specialty" value={doctor.specialty} onChange={handleChange} required />
        </label>
        <label>
          Location:
          <input type="text" name="location" value={doctor.location} onChange={handleChange} required />
        </label>
        <label>
          Hospital:
          <input type="text" name="hospital" value={doctor.hospital} onChange={handleChange} required />
        </label>
        <label>
          Email:
          <input type="email" name="email" value={doctor.email} onChange={handleChange} required />
        </label>
        <label>
          Phone:
          <input type="text" name="phone" value={doctor.phone} onChange={handleChange} required />
        </label>
        <label>
          Password:
          <input type="password" name="password" value={doctor.password} onChange={handleChange} required />
        </label>
        <button type="submit">Create Doctor</button>
      </form>
    </div>
  );
}

export default CreateDoctor;
