import React from 'react';
import './Home.css';

function Home() {
  return (
    <div className="home-container">
      <div className="home-overlay"></div> {/* Background overlay */}
      <div className="home-content">
        {/* Main Headline */}
        <h1>Trusted Healthcare, Tailored to You</h1>
        
        {/* Subheadline */}
        <h2>Your Health, Our Priority. Comprehensive Medical Services Delivered with Care.</h2>
        
        {/* Welcome Message */}
        <p>
          Welcome to <strong>[Your Platform Name]</strong>, where we redefine healthcare by combining 
          compassion, innovation, and excellence. Our mission is to empower individuals to take control 
          of their health journey with personalized, accessible, and trustworthy medical services.
        </p>
        
        {/* Key Features Section */}
        <h2>Why Choose Us?</h2>
        <ul>
          <li><strong>Comprehensive Medical Services:</strong> From preventive care and diagnostics to specialized treatments, we offer a wide range of healthcare solutions tailored to your needs.</li>
          <li><strong>Expert Medical Team:</strong> Our doctors, nurses, and specialists bring years of expertise and dedication to ensure you receive the best care.</li>
          <li><strong>Cutting-Edge Technology:</strong> Leveraging the latest advancements in medical technology for accurate diagnosis and effective treatments.</li>
          <li><strong>Patient-Centric Approach:</strong> We prioritize your comfort, convenience, and satisfaction at every step of your healthcare experience.</li>
        </ul>

        {/* Mission Statement */}
        <h2>Our Mission</h2>
        <p>
          To enhance the quality of life for individuals and communities by providing accessible, high-quality medical care with compassion and integrity.
        </p>
        
        {/* Call-to-Action */}
        <h2>Take Action</h2>
        <p>
          <strong>Book an Appointment:</strong> Take the first step towards better health. Schedule your appointment with our trusted medical team.<br />
          <strong>Contact Us:</strong> Have questions? We’re here to help. Reach out to us at [contact details].
        </p>
        
        {/* Testimonials Section (Optional) */}
        <h2>What Our Patients Say</h2>
        <blockquote>
          "Our experience at [Your Platform Name] was exceptional. The team was attentive, professional, and made us feel valued. Highly recommend!" – [Patient Name]
        </blockquote>
      </div>
    </div>
  );
}

export default Home;
