import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from '../api';  // Import Axios instance from api.js (set up in previous step)
import './Login.css';  // Make sure to import the Login styles

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // Make an API call to verify login credentials
      const response = await axios.post('/login', { username, password });

      // Check if login is successful
      if (response.data.success) {
        alert('Login successful!');
        localStorage.setItem('token', response.data.token);  // Save the token for authentication
        navigate('/dashboard');  // Redirect to dashboard
      } else {
        alert('Invalid username or password!');
      }
    } catch (error) {
      console.error('Login error:', error);
      alert('Login failed. Please try again later.');
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>Login</h2>
        <label htmlFor="username">Username</label>
        <input
          type="text"
          id="username"
          name="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
        <p>New user? <a href="/register">Register here</a></p>
      </form>
    </div>
  );
}

export default Login;
