import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../API/api'; // Ensure this is the correct path for the Axios instance
import './Register.css';

function Register() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('patient'); // Default role can be 'patient'
  const [showPassword, setShowPassword] = useState(false); // State to toggle password visibility
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Handle showing and hiding password
  const togglePassword = () => {
    setShowPassword((prev) => !prev);
  };

  // Handle form submission
  const handleRegister = (e) => {
    e.preventDefault();
  
    // Check if the email already exists in localStorage
    const existingUser = JSON.parse(localStorage.getItem('users')) || [];
    const emailExists = existingUser.find(user => user.email === email);
  
    if (emailExists) {
      setError('Email is already registered');
      return;
    }
  
    // Save the new user details to localStorage
    const newUser = { email, password, role };
    existingUser.push(newUser);
    localStorage.setItem('users', JSON.stringify(existingUser));
  
    alert('Registration successful!');
    navigate('/login'); // Redirect to login page after successful registration
  };
  
  return (
    <div className="register-container">
      <form className="register-form" onSubmit={handleRegister}>
        <h2>Register</h2>
        <label htmlFor="email">Email</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <label htmlFor="password">Password</label>
        <div className="password-container">
          <input
            type={showPassword ? 'text' : 'password'}
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="button" onClick={togglePassword} className="show-password-btn">
            {showPassword ? 'Hide' : 'Show'}
          </button>
        </div>
        <label htmlFor="role">Role</label>
        <select
          id="role"
          value={role}
          onChange={(e) => setRole(e.target.value)}
        >
          <option value="patient">Patient</option>
          <option value="doctor">Doctor</option>
        </select>
        <button type="submit">Register</button>
        {error && <p>{error}</p>}
      </form>
    </div>
  );
}

export default Register;
