import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // You can still use axios, but mock the responses for testing
import './Login.css';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  // Mock function to simulate an API call
  const handleLogin = async (e) => {
    e.preventDefault();

    // Mock response data (simulate a successful login)
    const mockResponse = {
      data: {
        success: true,
        message: 'Login successful!',
        username: 'admin',
      },
    };

    // Simulate an API call with a delay using setTimeout
    setTimeout(() => {
      if (username === 'admin' && password === 'password123') {
        alert(mockResponse.data.message); // Show success message
        navigate('/dashboard'); // Redirect to the dashboard
      } else {
        alert('Invalid username or password!');
      }
    }, 1000); // Simulate a 1-second delay for the API call
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>Login</h2>
        <label htmlFor="username">Username</label>
        <input
          type="text"
          id="username"
          name="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
        <p>New user? <a href="/register">Register here</a></p>
      </form>
    </div>
  );
}

export default Login;
