import React, { useState, useEffect } from "react";
import io from "socket.io-client"; // Import socket.io-client
import "./PatientDashboard.css";

const PatientDashboard = () => {
  const [activeTab, setActiveTab] = useState(""); // To toggle between tabs
  const [profileData, setProfileData] = useState(null); // Store patient profile
  const [appointmentHistory, setAppointmentHistory] = useState([]); // Store appointment history
  const [profileFormData, setProfileFormData] = useState({
    PatientID: "",
    PatientName: "",
    MobileNo: "",
    Email: "",
    Password: "",
    BloodGroup: "",
    Gender: "",
    Age: "",
    Address: "",
  });

  const [appointmentFormData, setAppointmentFormData] = useState({
    AppointmentID: "",
    PatientID: "",
    DoctorID: "",
    Date: "",
    TimeSlot: "",
    Remark: "",
  });

  const [socket, setSocket] = useState(null); // To store the WebSocket connection

  useEffect(() => {
    // Establish WebSocket connection when component mounts
    const newSocket = io("http://localhost:5000"); // Replace with your server URL
    setSocket(newSocket);

    // Listen for updates or notifications (e.g., new appointment)
    newSocket.on("appointmentUpdate", (data) => {
      console.log("New appointment update:", data);
      setAppointmentHistory((prevHistory) => [...prevHistory, data]);
    });

    // Cleanup socket connection when the component unmounts
    return () => newSocket.close();
  }, []);

  const handleLogout = () => {
    alert("Logged out successfully!");
    window.location.href = "/login"; // Redirect to login page
  };

  const handleProfileFormChange = (e) => {
    const { name, value } = e.target;
    setProfileFormData({ ...profileFormData, [name]: value });
  };

  const handleAppointmentFormChange = (e) => {
    const { name, value } = e.target;
    setAppointmentFormData({ ...appointmentFormData, [name]: value });
  };

  const handleProfileSubmit = (e) => {
    e.preventDefault();
    setProfileData(profileFormData); // Save profile data
    setProfileFormData({
      PatientID: "",
      PatientName: "",
      MobileNo: "",
      Email: "",
      Password: "",
      BloodGroup: "",
      Gender: "",
      Age: "",
      Address: "",
    });
    setActiveTab("patientProfile"); // After saving, show the profile info
    alert("Profile created successfully!");
  };

  const handleEditProfile = () => {
    setProfileFormData(profileData); // Load existing profile data into the form
    setActiveTab("createProfile"); // Open the profile creation form to edit
  };

  const handleDeleteProfile = () => {
    if (window.confirm("Are you sure you want to delete your profile?")) {
      setProfileData(null);
      alert("Profile deleted successfully!");
    }
  };

  const handleBookAppointment = (e) => {
    e.preventDefault();
    // Emit appointment to the server via WebSocket
    if (socket) {
      socket.emit("bookAppointment", appointmentFormData); // Emit appointment data
    }
    setAppointmentHistory([...appointmentHistory, appointmentFormData]); // Add appointment to history
    setAppointmentFormData({
      AppointmentID: "",
      PatientID: "",
      DoctorID: "",
      Date: "",
      TimeSlot: "",
      Remark: "",
    });
    setActiveTab("appointmentHistory"); // Navigate to history after booking
    alert("Appointment booked successfully!");
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
        {profileData ? (
          <button onClick={() => setActiveTab("patientProfile")}>
            Patient Profile
          </button>
        ) : (
          <button onClick={() => setActiveTab("createProfile")}>
            Create Profile
          </button>
        )}
        <button onClick={() => setActiveTab("bookAppointment")}>
          Book Appointment
        </button>
        <button onClick={() => setActiveTab("appointmentHistory")}>
          Appointment History
        </button>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <h1 className="dashboard-heading">Welcome to Patient Dashboard</h1>

        {/* Create Profile Form */}
        {activeTab === "createProfile" && (
          <div className="centered-form">
            <form onSubmit={handleProfileSubmit}>
              <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
                {profileData ? "Edit Patient Profile" : "Create Patient Profile"}
              </h2>
              <input
                type="text"
                name="PatientID"
                placeholder="Patient ID"
                value={profileFormData.PatientID}
                onChange={handleProfileFormChange}
                required
              />
              {/* Other form fields here */}
              <button type="submit">Submit</button>
            </form>
          </div>
        )}

        {/* Patient Profile */}
        {activeTab === "patientProfile" && profileData && (
          <div className="centered-form">
            <h2>Patient Profile</h2>
            {/* Display profile data */}
            <div className="profile-actions">
              <button onClick={handleEditProfile}>Edit Profile</button>
              <button
                onClick={handleDeleteProfile}
                style={{ background: "red", color: "white" }}
              >
                Delete Profile
              </button>
            </div>
          </div>
        )}

        {/* Book Appointment Form */}
        {activeTab === "bookAppointment" && (
          <div className="centered-form">
            <form onSubmit={handleBookAppointment}>
              <h2>Book Appointment</h2>
              {/* Appointment form fields here */}
              <button type="submit">Book Appointment</button>
            </form>
          </div>
        )}

        {/* Appointment History */}
        {activeTab === "appointmentHistory" && (
          <div className="centered-form">
            <h2>Appointment History</h2>
            {appointmentHistory.length > 0 ? (
              <table className="appointment-history-table">
                <thead>
                  <tr>
                    <th>Appointment ID</th>
                    <th>Patient ID</th>
                    <th>Doctor ID</th>
                    <th>Date</th>
                    <th>Time Slot</th>
                    <th>Remark</th>
                  </tr>
                </thead>
                <tbody>
                  {appointmentHistory.map((appointment, index) => (
                    <tr key={index}>
                      <td>{appointment.AppointmentID}</td>
                      <td>{appointment.PatientID}</td>
                      <td>{appointment.DoctorID}</td>
                      <td>{appointment.Date}</td>
                      <td>{appointment.TimeSlot}</td>
                      <td>{appointment.Remark}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p>No appointments found.</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default PatientDashboard;
