import React, { useState } from 'react';
import DoctorProfile from './DoctorProfile'; // Make sure the import is correct

const Doctordashboard = () => {
  const [showCreateProfile, setShowCreateProfile] = useState(false);
  const [profile, setProfile] = useState(null); // Store doctor profile data

  const handleCreateProfileClick = () => {
    setShowCreateProfile(true);
  };

  const handleProfileSubmit = (profileData) => {
    // Save the profile data to state
    setProfile(profileData);
    setShowCreateProfile(false); // Hide the form after submission
  };

  return (
    <div className="dashboard-container">
      <div className="sidebar">
        <button onClick={handleCreateProfileClick}>Create Profile</button>
      </div>

      <div className="content">
        {showCreateProfile ? (
          <DoctorProfile onSubmit={handleProfileSubmit} />
        ) : profile ? (
          <div>
            <h2>Doctor Profile</h2>
            <p><strong>Name:</strong> {profile.name}</p>
            <p><strong>Doctor ID:</strong> {profile.doctorId}</p>
            <p><strong>Experience:</strong> {profile.experience}</p>
            <p><strong>Specialty:</strong> {profile.specialty}</p>
            <p><strong>Location:</strong> {profile.location}</p>
            <p><strong>Hospital Name:</strong> {profile.hospitalName}</p>
            <p><strong>Email:</strong> {profile.email}</p>
            <p><strong>Mobile Number:</strong> {profile.mobileNumber}</p>
            <p><strong>Photo:</strong> <img src={profile.photo} alt="Doctor" /></p>
          </div>
        ) : (
          <div>Welcome to your Dashboard</div>
        )}
      </div>
    </div>
  );
};

export default Doctordashboard;
