import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './DoctorProfile.css'; // Optional, for styling

function DoctorProfile() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    doctorId: '',
    experience: '',
    specialty: '',
    location: '',
    hospital: '',
    email: '',
    mobile: '',
    photo: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: files[0],
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Profile Data:', formData);
    
    // Simulate saving data to backend or local storage
    
    // Redirect to Dashboard after form submission
    navigate('/Doctordashboard');
  };

  return (
    <div className="profile-container">
      <h2>Create Doctor Profile</h2>
      <form onSubmit={handleSubmit} className="profile-form">
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Doctor ID:
          <input
            type="text"
            name="doctorId"
            value={formData.doctorId}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Experience (Years):
          <input
            type="number"
            name="experience"
            value={formData.experience}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Specialty:
          <input
            type="text"
            name="specialty"
            value={formData.specialty}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Location:
          <input
            type="text"
            name="location"
            value={formData.location}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Hospital Name:
          <input
            type="text"
            name="hospital"
            value={formData.hospital}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Mobile Number:
          <input
            type="tel"
            name="mobile"
            value={formData.mobile}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Photo:
          <input
            type="file"
            name="photo"
            onChange={handleFileChange}
          />
        </label>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default DoctorProfile;
