import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import DoctorList from './sections/DoctorList'; // Doctor List Component
import CreateDoctor from './sections/CreateDoctor'; // Create Doctor Component
import './Dashboard.css'; // Dashboard styles

function Dashboard() {
  const [showDoctorForm, setShowDoctorForm] = useState(false);
  const [doctors, setDoctors] = useState([]);

  // Function to add a new doctor
  const addDoctor = (doctor) => {
    setDoctors((prevDoctors) => [...prevDoctors, doctor]);
    setShowDoctorForm(false); // Switch back to doctor list view
  };

  return (
    <div className="dashboard">
      <h1>Welcome to the Dashboard</h1>
      <ul className="dashboard-navigation">
        <li onClick={() => setShowDoctorForm(false)}><Link to="#">Doctor List</Link></li>
        <li onClick={() => setShowDoctorForm(true)}><Link to="#">Add Doctor</Link></li>
        <li><Link to="#">User List</Link></li>
        <li><Link to="#">Patient List</Link></li>
        <li><Link to="#">Admin List</Link></li>
        <li><Link to="#">Appointment List</Link></li>
        <li><Link to="#">Availability Dates</Link></li>
        <li><Link to="#">Feedback List</Link></li>
        <li><Link to="#">Logout</Link></li>
      </ul>

      <div className="dashboard-content">
        {showDoctorForm ? (
          <CreateDoctor addDoctor={addDoctor} />
        ) : (
          <DoctorList doctors={doctors} />
        )}
      </div>
    </div>
  );
}

export default Dashboard;
