import React, { useState } from 'react';  // Make sure to import useState

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');  // Add this line to declare setError

  const handleLogin = (e) => {
    e.preventDefault();

    // Example validation: check if email and password are not empty
    if (!email || !password) {
      setError('Email and password are required');
      return;
    }

    // Simulating a login check, you can add your actual validation here
    const user = JSON.parse(localStorage.getItem('users'))?.find(
      (user) => user.email === email && user.password === password
    );

    if (user) {
      localStorage.setItem('currentUser', JSON.stringify(user));  // Store logged-in user
      setError('');  // Clear error if login is successful
      alert('Login successful!');
      // Navigate to the dashboard or another page
    } else {
      setError('Invalid email or password');  // Show error message
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>Login</h2>
        <label htmlFor="username">Username</label>
        <input
          type="text"
          id="username"
          name="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
        <p>New user? <a href="/register">Register here</a></p>

        {/* Forgot Password button */}
        <button
          type="button"
          onClick={() => setShowForgotPassword(true)} // Trigger to show forgot password form
          className="forgot-password-button"
        >
          Forgot Password?
        </button>
      </form>

      {/* Forgot Password Form */}
      {showForgotPassword && (
        <div className="forgot-password-form">
          <h3>Enter your email to reset password</h3>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)} // Update the email state on change
            required
          />
          <button type="button" onClick={handleForgotPassword}>
            Send Reset Link
          </button>
          <button type="button" onClick={() => setShowForgotPassword(false)}>
            Cancel
          </button>
        </div>
      )}
    </div>
  );
}

export default Login;
