import React, { useState } from "react";
import "./Doctordashboard.css";

const Doctordashboard = () => {
  const [activeTab, setActiveTab] = useState(""); // To toggle between tabs
  const [profileData, setProfileData] = useState(null); // Store doctor profile
  const [availabilityList, setAvailabilityList] = useState([]); // Store doctor's availability
  const [profileFormData, setProfileFormData] = useState({
    name: "",
    specialization: "",
    experience: "",
    profilePhoto: null,
  });

  const [availabilityFormData, setAvailabilityFormData] = useState({
    doctorID: "",
    date: "",
    timeSlot: "",
    availabilityID: "",
  });

  const [settingsOpen, setSettingsOpen] = useState(false); // To toggle settings dropdown

  const handleLogout = () => {
    alert("Logged out successfully!");
    window.location.href = "/login"; // Redirect to login page
  };

  const handleProfileFormChange = (e) => {
    const { name, value } = e.target;
    setProfileFormData({ ...profileFormData, [name]: value });
  };

  const handleAvailabilityFormChange = (e) => {
    const { name, value } = e.target;
    setAvailabilityFormData({ ...availabilityFormData, [name]: value });
  };

  const handleProfileSubmit = (e) => {
    e.preventDefault();
    setProfileData(profileFormData); // Save profile data
    setProfileFormData({ name: "", specialization: "", experience: "", profilePhoto: null });
    setActiveTab(""); // Reset view
    alert("Profile created successfully!");
  };

  const handleAddAvailabilitySubmit = (e) => {
    e.preventDefault();
    setAvailabilityList([...availabilityList, availabilityFormData]); // Add availability
    setAvailabilityFormData({ doctorID: "", date: "", timeSlot: "", availabilityID: "" });
    setActiveTab(""); // Reset view
    alert("Availability added successfully!");
  };

  const handleEditProfile = () => {
    setProfileFormData(profileData); // Load existing profile data
    setActiveTab("createProfile");
  };

  const handleDeleteProfile = () => {
    if (window.confirm("Are you sure you want to delete your profile?")) {
      setProfileData(null);
      alert("Profile deleted successfully!");
    }
  };

  const handleEditAvailability = (index) => {
    setAvailabilityFormData(availabilityList[index]); // Load existing availability data
    setActiveTab("addAvailability");
  };

  const handleDeleteAvailability = (index) => {
    if (window.confirm("Are you sure you want to delete this availability?")) {
      const updatedList = availabilityList.filter((_, i) => i !== index);
      setAvailabilityList(updatedList);
      alert("Availability deleted successfully!");
    }
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
        <button onClick={() => setActiveTab("createProfile")}>Create Profile</button>
        <button onClick={() => setActiveTab("addAvailability")}>Add Availability</button>
        <button onClick={() => setActiveTab("viewAppointments")}>View Appointments</button>

        {/* Settings Dropdown */}
        <div className="settings" onClick={() => setSettingsOpen(!settingsOpen)}>
          <button>Settings</button>
          {settingsOpen && (
            <div className="settings-dropdown">
              <button onClick={handleEditProfile}>Edit Profile</button>
              <button onClick={handleEditAvailability}>Edit Availability</button>
              <button onClick={handleDeleteProfile} style={{ background: "red", color: "white" }}>
                Delete Profile
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Create Profile Form */}
        {activeTab === "createProfile" && (
          <div className="centered-form">
            <form onSubmit={handleProfileSubmit}>
              <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
                {profileData ? "Edit Doctor Profile" : "Create Doctor Profile"}
              </h2>
              <input
                type="text"
                name="name"
                placeholder="Name"
                value={profileFormData.name}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="specialization"
                placeholder="Specialization"
                value={profileFormData.specialization}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="experience"
                placeholder="Experience (in years)"
                value={profileFormData.experience}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="file"
                name="profilePhoto"
                onChange={(e) =>
                  setProfileFormData({ ...profileFormData, profilePhoto: e.target.files[0] })
                }
              />
              <button type="submit">
                {profileData ? "Update Profile" : "Submit"}
              </button>
            </form>
          </div>
        )}

        {/* Add Availability Form */}
        {activeTab === "addAvailability" && (
          <div className="centered-form">
            <form onSubmit={handleAddAvailabilitySubmit}>
              <h2 style={{ textAlign: "center", marginBottom: "20px" }}>Add Availability</h2>
              <input
                type="text"
                name="doctorID"
                placeholder="Doctor ID"
                value={availabilityFormData.doctorID}
                onChange={handleAvailabilityFormChange}
                required
              />
              <input
                type="date"
                name="date"
                value={availabilityFormData.date}
                onChange={handleAvailabilityFormChange}
                required
              />
              <input
                type="time"
                name="timeSlot"
                value={availabilityFormData.timeSlot}
                onChange={handleAvailabilityFormChange}
                required
              />
              <input
                type="text"
                name="availabilityID"
                placeholder="Availability ID"
                value={availabilityFormData.availabilityID}
                onChange={handleAvailabilityFormChange}
                required
              />
              <button type="submit">Add Availability</button>
            </form>
          </div>
        )}

        {/* Display Doctor Profile */}
        {!activeTab && profileData && (
          <div className="profile-section">
            <h2>Doctor Profile</h2>
            <p><strong>Name:</strong> {profileData.name}</p>
            <p><strong>Specialization:</strong> {profileData.specialization}</p>
            <p><strong>Experience:</strong> {profileData.experience} years</p>
            {profileData.profilePhoto && (
              <img
                src={URL.createObjectURL(profileData.profilePhoto)}
                alt="Profile"
                style={{ width: "150px", borderRadius: "50%" }}
              />
            )}
            <div className="profile-actions">
              <button onClick={handleEditProfile}>Edit Profile</button>
              <button onClick={handleDeleteProfile} style={{ background: "red", color: "white" }}>
                Delete Profile
              </button>
            </div>
          </div>
        )}

        {/* Display Availability */}
        {!activeTab && availabilityList.length > 0 && (
          <div className="availability-section">
            <h2>Doctor Availability</h2>
            {availabilityList.map((availability, index) => (
              <div key={index} className="availability-card">
                <p><strong>Doctor ID:</strong> {availability.doctorID}</p>
                <p><strong>Date:</strong> {availability.date}</p>
                <p><strong>Time Slot:</strong> {availability.timeSlot}</p>
                <p><strong>Availability ID:</strong> {availability.availabilityID}</p>
                <div className="availability-actions">
                  <button onClick={() => handleEditAvailability(index)}>Edit Availability</button>
                  <button onClick={() => handleDeleteAvailability(index)} style={{ background: "red", color: "white" }}>
                    Delete Availability
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Doctordashboard;
