import React, { useState } from "react";
import { PieChart, Pie, Cell, Tooltip, Legend } from "recharts";
import "./Doctordashboard.css";

const Doctordashboard = () => {
  const [activeTab, setActiveTab] = useState("dashboard");
  const [profileData, setProfileData] = useState(null);
  const [availabilityList, setAvailabilityList] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [patients, setPatients] = useState([]);

  const appointmentStats = {
    scheduled: appointments.filter((a) => a.status === "scheduled").length,
    completed: appointments.filter((a) => a.status === "completed").length,
    canceled: appointments.filter((a) => a.status === "canceled").length,
  };

  const pieData = [
    { name: "Scheduled", value: appointmentStats.scheduled },
    { name: "Completed", value: appointmentStats.completed },
    { name: "Canceled", value: appointmentStats.canceled },
  ];
  const COLORS = ["#8884d8", "#82ca9d", "#ff4d4d"];

  const renderSidebar = () => (
    <div className="sidebar">
      <h2>Doctor Dashboard</h2>
      <ul>
        <li onClick={() => setActiveTab("dashboard")}>Dashboard</li>
        <li onClick={() => setActiveTab("profile")}>Profile</li>
        <li onClick={() => setActiveTab("appointments")}>Appointments</li>
        <li onClick={() => setActiveTab("patients")}>Patients</li>
        <li onClick={() => setActiveTab("availability")}>Availability</li>
        <li onClick={() => setActiveTab("chat")}>Chat</li>
        <li>
          <div className="dropdown">
            Settings
            <div className="dropdown-content">
              <button onClick={() => setActiveTab("profile")}>Edit Profile</button>
              <button onClick={() => setActiveTab("availability")}>
                Edit Availability
              </button>
              <button onClick={() => alert("Profile Deleted!")}>Delete Profile</button>
              <button onClick={() => alert("Logged Out!")}>Logout</button>
            </div>
          </div>
        </li>
      </ul>
    </div>
  );

  const renderDashboard = () => (
    <div className="dashboard-content">
      <h2>Welcome, {profileData?.name || "Doctor"}!</h2>
      <div className="stats-container">
        <div className="stat-box">Total Appointments: {appointments.length}</div>
        <div className="stat-box">Total Patients: {patients.length}</div>
        <div className="stat-box">Upcoming Scheduled: {appointmentStats.scheduled}</div>
      </div>
      <div className="chart-container">
        <h3>Appointment Statistics</h3>
        <PieChart width={400} height={400}>
          <Pie
            data={pieData}
            cx="50%"
            cy="50%"
            outerRadius={100}
            fill="#8884d8"
            dataKey="value"
            label
          >
            {pieData.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
            ))}
          </Pie>
          <Tooltip />
          <Legend />
        </PieChart>
      </div>
    </div>
  );

  const renderProfile = () => (
    <div className="profile-form">
      <h2>Doctor Profile</h2>
      {!profileData ? (
        <form
          onSubmit={(e) => {
            e.preventDefault();
            const formData = new FormData(e.target);
            setProfileData(Object.fromEntries(formData.entries()));
          }}
        >
          <input name="name" placeholder="Name" required />
          <input name="specialization" placeholder="Specialization" required />
          <input name="experience" placeholder="Experience (Years)" required />
          <button type="submit">Submit</button>
        </form>
      ) : (
        <div>
          <p>Name: {profileData.name}</p>
          <p>Specialization: {profileData.specialization}</p>
          <p>Experience: {profileData.experience} years</p>
          <button
            onClick={() => {
              setProfileData(null);
            }}
          >
            Edit
          </button>
        </div>
      )}
    </div>
  );

  const renderAvailability = () => (
    <div className="availability-form">
      <h2>Availability</h2>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          const formData = new FormData(e.target);
          const newSlot = Object.fromEntries(formData.entries());
          setAvailabilityList([...availabilityList, newSlot]);
        }}
      >
        <input name="from" type="time" required />
        <input name="to" type="time" required />
        <button type="submit">Add Slot</button>
      </form>
      <ul>
        {availabilityList.map((slot, index) => (
          <li key={index}>
            From: {slot.from} - To: {slot.to}
            <button
              onClick={() => {
                setAvailabilityList(
                  availabilityList.filter((_, i) => i !== index)
                );
              }}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );

  const renderAppointments = () => (
    <div className="appointments-list">
      <h2>Appointments</h2>
      {appointments.map((appointment, index) => (
        <div key={index} className="appointment-item">
          <p>Patient: {appointment.patient}</p>
          <p>Status: {appointment.status}</p>
          <button
            onClick={() => {
              const updated = [...appointments];
              updated[index].status = "completed";
              setAppointments(updated);
            }}
          >
            Mark as Completed
          </button>
          <button
            onClick={() => {
              const updated = [...appointments];
              updated[index].status = "canceled";
              setAppointments(updated);
            }}
          >
            Cancel
          </button>
        </div>
      ))}
    </div>
  );

  const renderPatients = () => (
    <div className="patients-list">
      <h2>Patients</h2>
      <ul>
        {patients.map((patient, index) => (
          <li key={index}>{patient.name}</li>
        ))}
      </ul>
    </div>
  );

  const renderChat = () => <h2>Chat Functionality Coming Soon!</h2>;

  return (
    <div className="dashboard-container">
      {renderSidebar()}
      <div className="main-content">
        {activeTab === "dashboard" && renderDashboard()}
        {activeTab === "profile" && renderProfile()}
        {activeTab === "availability" && renderAvailability()}
        {activeTab === "appointments" && renderAppointments()}
        {activeTab === "patients" && renderPatients()}
        {activeTab === "chat" && renderChat()}
      </div>
    </div>
  );
};

export default Doctordashboard;
