import React, { useState } from "react";

const Doctordashboard = () => {
  const [activeTab, setActiveTab] = useState("none");
  const [profileData, setProfileData] = useState(null);
  const [availability, setAvailability] = useState([]);
  const [appointments, setAppointments] = useState([
    { patient: "John Doe", date: "2024-12-01", time: "10:00 AM" },
    { patient: "Jane Smith", date: "2024-12-02", time: "11:30 AM" },
  ]);

  const [formData, setFormData] = useState({
    name: "",
    specialization: "",
    experience: "",
    profilePhoto: "",
  });

  const handleFormChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleProfileSubmit = (e) => {
    e.preventDefault();
    setProfileData(formData);
    setActiveTab("none");
    alert("Profile Created Successfully!");
  };

  const handleAddAvailability = (e) => {
    e.preventDefault();
    const { day, time } = e.target.elements;
    setAvailability([...availability, { day: day.value, time: time.value }]);
    e.target.reset();
  };

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      {/* Sidebar */}
      <div
        style={{
          width: "250px",
          backgroundColor: "#2c3e50",
          color: "white",
          display: "flex",
          flexDirection: "column",
          padding: "20px",
        }}
      >
        <h2 style={{ textAlign: "center", marginBottom: "20px" }}>Dashboard</h2>
        <button
          onClick={() => setActiveTab("createProfile")}
          style={{
            margin: "10px 0",
            padding: "10px",
            backgroundColor: "#34495e",
            color: "white",
            border: "none",
            cursor: "pointer",
          }}
        >
          Create Profile
        </button>
        <button
          onClick={() => setActiveTab("availability")}
          style={{
            margin: "10px 0",
            padding: "10px",
            backgroundColor: "#34495e",
            color: "white",
            border: "none",
            cursor: "pointer",
          }}
        >
          Add Availability
        </button>
        <button
          onClick={() => setActiveTab("appointments")}
          style={{
            margin: "10px 0",
            padding: "10px",
            backgroundColor: "#34495e",
            color: "white",
            border: "none",
            cursor: "pointer",
          }}
        >
          View Appointments
        </button>
        <button
          onClick={() => setActiveTab("settings")}
          style={{
            margin: "10px 0",
            padding: "10px",
            backgroundColor: "#34495e",
            color: "white",
            border: "none",
            cursor: "pointer",
          }}
        >
          Settings
        </button>
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: "20px", backgroundColor: "#f9f9f9" }}>
        {activeTab === "createProfile" && (
          <div>
            <h2>Create Doctor Profile</h2>
            <form
              onSubmit={handleProfileSubmit}
              style={{
                display: "flex",
                flexDirection: "column",
                gap: "10px",
                maxWidth: "400px",
              }}
            >
              <input
                type="text"
                name="name"
                placeholder="Name"
                value={formData.name}
                onChange={handleFormChange}
              />
              <input
                type="text"
                name="specialization"
                placeholder="Specialization"
                value={formData.specialization}
                onChange={handleFormChange}
              />
              <input
                type="text"
                name="experience"
                placeholder="Experience (in years)"
                value={formData.experience}
                onChange={handleFormChange}
              />
              <input
                type="file"
                name="profilePhoto"
                onChange={(e) =>
                  setFormData({ ...formData, profilePhoto: e.target.files[0] })
                }
              />
              <button type="submit" style={{ padding: "10px", cursor: "pointer" }}>
                Submit
              </button>
            </form>
          </div>
        )}

        {activeTab === "availability" && (
          <div>
            <h2>Add Availability</h2>
            <form
              onSubmit={handleAddAvailability}
              style={{
                display: "flex",
                flexDirection: "column",
                gap: "10px",
                maxWidth: "400px",
              }}
            >
              <input type="text" name="day" placeholder="Day (e.g., Monday)" />
              <input
                type="text"
                name="time"
                placeholder="Time Slot (e.g., 10:00 AM - 11:00 AM)"
              />
              <button type="submit" style={{ padding: "10px", cursor: "pointer" }}>
                Add
              </button>
            </form>
            <ul>
              {availability.map((slot, index) => (
                <li key={index}>
                  {slot.day} - {slot.time}
                </li>
              ))}
            </ul>
          </div>
        )}

        {activeTab === "appointments" && (
          <div>
            <h2>View Appointments</h2>
            <ul>
              {appointments.map((appointment, index) => (
                <li key={index}>
                  {appointment.patient} - {appointment.date} - {appointment.time}
                </li>
              ))}
            </ul>
          </div>
        )}

        {activeTab === "settings" && (
          <div>
            <h2>Settings</h2>
            {profileData ? (
              <div>
                <p>
                  <strong>Name:</strong> {profileData.name}
                </p>
                <p>
                  <strong>Specialization:</strong> {profileData.specialization}
                </p>
                <p>
                  <strong>Experience:</strong> {profileData.experience} years
                </p>
                <button
                  style={{ padding: "10px", cursor: "pointer", marginRight: "10px" }}
                >
                  Edit Profile
                </button>
                <button
                  style={{
                    padding: "10px",
                    cursor: "pointer",
                    backgroundColor: "red",
                    color: "white",
                  }}
                >
                  Delete Profile
                </button>
              </div>
            ) : (
              <p>No profile data available. Please create a profile first.</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Doctordashboard;
