import React, { useEffect, useState } from "react";

const PatientDashboard = () => {
  const [doctorProfiles, setDoctorProfiles] = useState([]);

  useEffect(() => {
    const storedProfiles = JSON.parse(localStorage.getItem("doctorProfiles")) || [];
    setDoctorProfiles(storedProfiles);
  }, []);

  return (
    <div className="patient-dashboard">
      <h1>Patient Dashboard</h1>
      <button
        onClick={() => setDoctorProfiles([...doctorProfiles])}
        style={{ margin: "20px 0", padding: "10px 20px", cursor: "pointer" }}
      >
        View Doctor List
      </button>

      <div className="doctor-list">
        {doctorProfiles.length > 0 ? (
          doctorProfiles.map((doctor, index) => (
            <div key={index} className="doctor-card">
              <h2>{doctor.name}</h2>
              <p><strong>Doctor ID:</strong> {doctor.DoctorID}</p>
              <p><strong>Specialization:</strong> {doctor.specialization}</p>
              <p><strong>Experience:</strong> {doctor.experience} years</p>
              <p><strong>Location:</strong> {doctor.Location}</p>
              <p><strong>Hospital:</strong> {doctor.Hospitalname}</p>
              <p><strong>Email:</strong> {doctor.Email}</p>
              <p><strong>Mobile No:</strong> {doctor.MobileNo}</p>
            </div>
          ))
        ) : (
          <p>No doctor profiles available.</p>
        )}
      </div>
    </div>
  );
};

export default PatientDashboard;
