import React, { useState } from 'react';
import './CreateDoctor.css';  // Importing the CSS file

function CreateDoctor({ addDoctor }) {
  // State to hold the new doctor's data
  const [doctor, setDoctor] = useState({
    id: '',
    name: '',
    specialty: '',
    location: '',
    hospital: '',
    email: '',
    phone: '',
    password: ''
  });

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setDoctor({
      ...doctor,
      [name]: value
    });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (doctor.password === '') {
      alert('Password is required!');
      return;
    }
    addDoctor(doctor); // Passing the new doctor data to the parent component
    setDoctor({
      id: '',
      name: '',
      specialty: '',
      location: '',
      hospital: '',
      email: '',
      phone: '',
      password: ''
    });
  };

  return (
    <div className="create-doctor-container">
      <h2>Create New Doctor</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Doctor ID:
          <input
            type="text"
            name="id"
            value={doctor.id}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Name:
          <input
            type="text"
            name="name"
            value={doctor.name}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Specialty:
          <input
            type="text"
            name="specialty"
            value={doctor.specialty}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Location:
          <input
            type="text"
            name="location"
            value={doctor.location}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Hospital:
          <input
            type="text"
            name="hospital"
            value={doctor.hospital}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Email:
          <input
            type="email"
            name="email"
            value={doctor.email}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Phone:
          <input
            type="text"
            name="phone"
            value={doctor.phone}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Password:
          <input
            type="password"
            name="password"
            value={doctor.password}
            onChange={handleChange}
            required
          />
        </label>

        <button type="submit" className="create-doctor-button">Create Doctor</button>
      </form>
    </div>
  );
}

export default CreateDoctor;
