// Doctordashboard.js
import React, { useState } from 'react';
 // Import the profile form component
import DoctorProfile from '.DoctorProfile';
function Doctordashboard() {
  const [showCreateProfile, setShowCreateProfile] = useState(false); // State to toggle profile form visibility

  const handleCreateProfileClick = () => {
    setShowCreateProfile(true); // Show the create profile form when clicked
  };

  return (
    <div className="dashboard-container">
      <div className="sidebar">
        {/* Sidebar content */}
        <ul>
          <li>Dashboard</li>
          <li onClick={handleCreateProfileClick}>Create Profile</li>
          {/* Other sidebar items */}
        </ul>
      </div>
      <div className="content">
        <h1>Welcome, Doctor!</h1>

        {/* Conditionally render the DoctorProfile component */}
        {showCreateProfile ? (
          <DoctorProfile />
        ) : (
          <div>
            <p>Select an option from the sidebar</p>
            {/* You can add other content or default page view here */}
          </div>
        )}
      </div>
    </div>
  );
}

export default Doctordashboard;
