// src/components/Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';

import './Login.css';
function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();

    // Fetch user data from localStorage
    const storedUser = JSON.parse(localStorage.getItem('user'));

    // Admin login bypass
    if (username === 'Admin' && password === 'adminpassword') {
      alert('Admin login successful!');
      navigate('/dashboard');
    }
    // Check other user roles (Patient or Doctor)
    else if (storedUser && storedUser.username === username && storedUser.password === password) {
      alert(`${storedUser.role} login successful!`);
      navigate('/dashboard');
    } else {
      alert('Invalid username or password!');
    }
  };

  const handleForgotPassword = () => {
    alert('Forgot password link has been sent to your email.');
  };

  return (
    <div className="login-container">
      <form onSubmit={handleLogin}>
        <h2>Login</h2>
        <label htmlFor="username">Username</label>
        <input
          type="text"
          id="username"
          name="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
        <p>
  New user? <Link to="/register">Register here</Link>
</p>
<Link to="/forgot-password">Forgot Password?</Link>
      </form>
    </div>
  );
}

export default Login;
