import React, { useState } from "react";
import "./Doctordashboard.css";

const Doctordashboard = () => {
  const [activeTab, setActiveTab] = useState(""); // To toggle between tabs
  const [profileData, setProfileData] = useState(null); // Store doctor profile
  const [availabilityList, setAvailabilityList] = useState([]); // Store doctor's availability
  const [prescriptions, setPrescriptions] = useState([]); // Store prescriptions
  const [profileFormData, setProfileFormData] = useState({
    name: "",
    DoctorID: "",
    specialization: "",
    experience: "",
    Location:"",
    Hospitalname:"",
    Email:"",
    MoblieNo:"",
    profilePhoto: null,
  });

  const [availabilityFormData, setAvailabilityFormData] = useState({
    doctorID: "",
    availabilityID: "",
    startDate: "",
    endDate: "",
    Time:"",
    index: null, // Track the index of the item to edit
  });

  const [prescriptionFormData, setPrescriptionFormData] = useState({
    doctorName: "",
    doctorContact: "",
    patientName: "",
    patientAge: "",
    patientGender: "",
    prescriptionDate: "",
    doctorSignature: "",
    medications: [{ medicineName: "", dosage: "", directions: "", quantity: "" }]
  });
  

  const handleLogout = () => {
    alert("Logged out successfully!");
    window.location.href = "/login"; // Redirect to login page
  };

  const handleProfileFormChange = (e) => {
    const { name, value } = e.target;
    setProfileFormData({ ...profileFormData, [name]: value });
  };

  const handleAvailabilityFormChange = (e) => {
    const { name, value } = e.target;
    setAvailabilityFormData({ ...availabilityFormData, [name]: value });
  };

  const handlePrescriptionFormChange = (e) => {
    const { name, value } = e.target;
    setPrescriptionFormData({ ...prescriptionFormData, [name]: value });
  };

  const handleProfileSubmit = (e) => {
    e.preventDefault();
    setProfileData(profileFormData); // Save profile data
    setProfileFormData({ name: "", DoctorID:"", specialization: "", experience: "",Location:"",Hospitalname:"",Email:"",MoblieNO:"", profilePhoto: null });
    setActiveTab("doctorProfile"); // After saving, show the profile info
    alert("Profile created successfully!");
  };

  const handleAddAvailabilitySubmit = (e) => {
    e.preventDefault();
    if (availabilityFormData.index !== null) {
      // Edit existing availability
      const updatedList = [...availabilityList];
      updatedList[availabilityFormData.index] = availabilityFormData;
      setAvailabilityList(updatedList);
      setAvailabilityFormData({
        doctorID: "",
        availabilityID: "",
        startDate: "",
        endDate: "",
        Time: "",
        index: null,
      });
    } else {
      // Add new availability
      setAvailabilityList([...availabilityList, availabilityFormData]);
    }
    setActiveTab(""); // Reset view
    alert("Availability updated successfully!");
  };

  const handleAddPrescriptionSubmit = (e) => {
    e.preventDefault();
    const newPrescription = {
      patientID: prescriptionFormData.patientID,
      doctorID: prescriptionFormData.doctorID,
      medication: prescriptionFormData.medication,
    };
    setPrescriptions([...prescriptions, newPrescription]);
    setPrescriptionFormData({ patientID: "", doctorID: "", medication: "" });
    alert("Prescription sent successfully!");
  };

  const handleEditProfile = () => {
    setProfileFormData(profileData); // Load existing profile data into the form
    setActiveTab("createProfile"); // Open the profile creation form to edit
  };

  const handleDeleteProfile = () => {
    if (window.confirm("Are you sure you want to delete your profile?")) {
      setProfileData(null);
      alert("Profile deleted successfully!");
    }
  };

  const handleEditAvailability = (index) => {
    setAvailabilityFormData({
      ...availabilityList[index],
      index: index, // Track the index of the availability to be edited
    });

    setActiveTab("addAvailability"); // Open the availability form to edit
  };

  const handleDeleteAvailability = (index) => {
    if (window.confirm("Are you sure you want to delete this availability?")) {
      const updatedList = availabilityList.filter((_, i) => i !== index);
      setAvailabilityList(updatedList);
      alert("Availability deleted successfully!");
    }
  };

  return (
    <div className="dashboard-container">
      <div className="sidebar">
        <div className="sidebar-header">
          <h2 style={{ color: '#fff', fontWeight: 'bold', fontSize: '20px' }}> DOCTOR DASHBOARD</h2>
        </div>
        <button onClick={() => setActiveTab("Dashboard")}>
          <i className="fas fa-tachometer-alt"></i> Dashboard
        </button>
        {profileData ? (
          <button onClick={() => setActiveTab("doctorProfile")}>
            <i className="fas fa-user-md"></i> Doctor Profile
          </button>
        ) : (
          <button onClick={() => setActiveTab("createProfile")}>
            <i className="fas fa-user-plus"></i> Create Profile
          </button>
        )}
        {availabilityList.length > 0 ? (
          <button onClick={() => setActiveTab("viewAvailability")}>
            <i className="fas fa-calendar-check"></i> View Availability
          </button>
        ) : (
          <button onClick={() => setActiveTab("addAvailability")}>
            <i className="fas fa-calendar-plus"></i> Add Availability
          </button>
        )}
        <button onClick={() => setActiveTab("viewAppointments")}>
          <i className="fas fa-clipboard-list"></i> View Appointments
        </button>
        <button onClick={() => setActiveTab("prescription")}>
          <i className="fas fa-prescription"></i> Prescription
        </button>
        <button className="logout-button" onClick={handleLogout}>
          <i className="fas fa-sign-out-alt"></i> Logout
        </button>
      </div>

      {/* Main Content */}
      <div>
        <header className="header">
          <h1 className="dashboard-heading">Welcome Doctor!</h1>
        </header>

        {/* Dashboard Information */}
        {activeTab === "Dashboard" && (
          <div className="dashboard-content" style={{ display: 'flex', justifyContent: 'space-between', gap: '20px', flexWrap: 'wrap' }}>
            {/* Dashboard Boxes */}
          </div>
        )}

        {/* Create Profile Form or Profile Info */}
        {activeTab === "createProfile" && (
          <div className="centered-form">
            {/* Profile form */}
          </div>
        )}

        {/* Doctor Profile Info */}
        {activeTab === "doctorProfile" && profileData && (
          <div className="centered-form">
            {/* Doctor profile details */}
          </div>
        )}

        {/* Add/Edit Availability Form or Availability Info */}
        {activeTab === "addAvailability" && (
          <div className="centered-form">
            {/* Availability form */}
          </div>
        )}

        {/* View Availability List */}
        {activeTab === "viewAvailability" && availabilityList.length > 0 && (
          <div className="availability-list">
            {/* Availability list */}
          </div>
        )}

{activeTab === "prescription" && (
  <div className="centered-form">
    <form onSubmit={handleAddPrescriptionSubmit}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
        Add Prescription
      </h2>

      {/* Doctor's Information */}
      <input
        type="text"
        name="doctorName"
        placeholder="Doctor's Name & Qualifications"
        value={prescriptionFormData.doctorName}
        onChange={handlePrescriptionFormChange}
        required
      />
      <input
        type="text"
        name="doctorContact"
        placeholder="Doctor's Contact Info (Phone/Address)"
        value={prescriptionFormData.doctorContact}
        onChange={handlePrescriptionFormChange}
        required
      />

      {/* Patient's Information */}
      <input
        type="text"
        name="patientName"
        placeholder="Patient's Name"
        value={prescriptionFormData.patientName}
        onChange={handlePrescriptionFormChange}
        required
      />
      <input
        type="text"
        name="patientAge"
        placeholder="Patient's Age/Date of Birth"
        value={prescriptionFormData.patientAge}
        onChange={handlePrescriptionFormChange}
        required
      />
      <input
        type="text"
        name="patientGender"
        placeholder="Patient's Gender (Optional)"
        value={prescriptionFormData.patientGender}
        onChange={handlePrescriptionFormChange}
      />
      <input
        type="date"
        name="prescriptionDate"
        value={prescriptionFormData.prescriptionDate}
        onChange={handlePrescriptionFormChange}
        required
      />

      {/* Medication Details */}
      <input
        type="text"
        name="medicineName"
        placeholder="Medicine Name (Brand/Generic)"
        value={prescriptionFormData.medicineName}
        onChange={handlePrescriptionFormChange}
        required
      />
      <input
        type="text"
        name="dosage"
        placeholder="Dosage (e.g., 500mg)"
        value={prescriptionFormData.dosage}
        onChange={handlePrescriptionFormChange}
        required
      />
      <textarea
        name="directions"
        placeholder="Directions for Use (e.g., take one tablet twice daily)"
        value={prescriptionFormData.directions}
        onChange={handlePrescriptionFormChange}
        required
      />
      <input
        type="text"
        name="quantity"
        placeholder="Quantity (e.g., 30 tablets)"
        value={prescriptionFormData.quantity}
        onChange={handlePrescriptionFormChange}
        required
      />

      {/* Doctor's Signature */}
      <input
        type="text"
        name="doctorSignature"
        placeholder="Doctor's Signature"
        value={prescriptionFormData.doctorSignature}
        onChange={handlePrescriptionFormChange}
        required
      />

      <button type="submit">Send Prescription</button>
    </form>
  </div>
)}

{/* View Prescriptions */}
{activeTab === "prescription" && (
  <div className="centered-form">
    <form onSubmit={handleAddPrescriptionSubmit}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
        Add Prescription
      </h2>

      {/* Doctor's Information */}
      <input
        type="text"
        name="doctorName"
        placeholder="Doctor's Name & Qualifications"
        value={prescriptionFormData.doctorName}
        onChange={handlePrescriptionFormChange}
        required
      />
      <input
        type="text"
        name="doctorContact"
        placeholder="Doctor's Contact Info (Phone/Address)"
        value={prescriptionFormData.doctorContact}
        onChange={handlePrescriptionFormChange}
        required
      />

      {/* Patient's Information */}
      <input
        type="text"
        name="patientName"
        placeholder="Patient's Name"
        value={prescriptionFormData.patientName}
        onChange={handlePrescriptionFormChange}
        required
      />
      <input
        type="text"
        name="patientAge"
        placeholder="Patient's Age/Date of Birth"
        value={prescriptionFormData.patientAge}
        onChange={handlePrescriptionFormChange}
        required
      />
      <input
        type="text"
        name="patientGender"
        placeholder="Patient's Gender (Optional)"
        value={prescriptionFormData.patientGender}
        onChange={handlePrescriptionFormChange}
      />
      <input
        type="date"
        name="prescriptionDate"
        value={prescriptionFormData.prescriptionDate}
        onChange={handlePrescriptionFormChange}
        required
      />

      {/* Medication Table */}
      <table>
        <thead>
          <tr>
            <th>Medicine Name</th>
            <th>Dosage</th>
            <th>Directions</th>
            <th>Quantity</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {prescriptionFormData.medications.map((medication, index) => (
            <tr key={index}>
              <td>
                <input
                  type="text"
                  name={`medicineName-${index}`}
                  placeholder="Medicine Name (Brand/Generic)"
                  value={medication.medicineName}
                  onChange={(e) => handleMedicationChange(e, index)}
                  required
                />
              </td>
              <td>
                <input
                  type="text"
                  name={`dosage-${index}`}
                  placeholder="Dosage (e.g., 500mg)"
                  value={medication.dosage}
                  onChange={(e) => handleMedicationChange(e, index)}
                  required
                />
              </td>
              <td>
                <textarea
                  name={`directions-${index}`}
                  placeholder="Directions for Use"
                  value={medication.directions}
                  onChange={(e) => handleMedicationChange(e, index)}
                  required
                />
              </td>
              <td>
                <input
                  type="number"
                  name={`quantity-${index}`}
                  placeholder="Quantity"
                  value={medication.quantity}
                  onChange={(e) => handleMedicationChange(e, index)}
                  required
                />
              </td>
              <td>
                <button type="button" onClick={() => removeMedication(index)}>
                  Remove
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add Medication Button */}
      <button type="button" onClick={addMedication}>
        Add Medication
      </button>

      {/* Doctor's Signature */}
      <input
        type="text"
        name="doctorSignature"
        placeholder="Doctor's Signature"
        value={prescriptionFormData.doctorSignature}
        onChange={handlePrescriptionFormChange}
        required
      />

      <button type="submit">Send Prescription</button>
    </form>
  </div>
)}

{/* View Prescriptions */}
{activeTab === "viewPrescriptions" && prescriptions.length > 0 && (
  <div className="prescription-list">
    <h2>Prescriptions</h2>
    {prescriptions.map((prescription, index) => (
      <div key={index} className="prescription-item">
        <p><strong>Patient Name:</strong> {prescription.patientName}</p>
        <p><strong>Doctor Name:</strong> {prescription.doctorName}</p>
        <p><strong>Prescription Date:</strong> {prescription.prescriptionDate}</p>
        <p><strong>Doctor's Signature:</strong> {prescription.doctorSignature}</p>

        {/* Medication Table View */}
        <h3>Medications</h3>
        <table>
          <thead>
            <tr>
              <th>Medicine Name</th>
              <th>Dosage</th>
              <th>Directions</th>
              <th>Quantity</th>
            </tr>
          </thead>
          <tbody>
            {prescription.medications.map((medication, idx) => (
              <tr key={idx}>
                <td>{medication.medicineName}</td>
                <td>{medication.dosage}</td>
                <td>{medication.directions}</td>
                <td>{medication.quantity}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    ))}
  </div>
)}

      </div>
    </div>
  );
};

export default Doctordashboard;
