// src/components/DoctorDashboard.jsx
import React, { useState } from 'react';
import './Doctordashboard.css';

const DoctorDashboard = () => {
  const [appointments, setAppointments] = useState([
    {
      id: 1,
      patientName: 'John Doe',
      date: '2024-11-25',
      time: '10:00 AM',
      status: 'Confirmed',
    },
    {
      id: 2,
      patientName: 'Jane Smith',
      date: '2024-11-26',
      time: '2:00 PM',
      status: 'Pending',
    },
  ]);

  const [availability, setAvailability] = useState({
    days: 'Monday to Friday',
    time: '9:00 AM - 5:00 PM',
  });

  return (
    <div className="doctor-dashboard">
      <div className="profile-section">
        <img
          src="https://via.placeholder.com/150" // Placeholder profile photo
          alt="Doctor"
          className="profile-photo"
        />
        <h2>Dr. Alex Johnson</h2>
        <p>Specialization: Cardiologist</p>
        <p>
          Availability: {availability.days}, {availability.time}
        </p>
      </div>

      <div className="appointments-section">
        <h3>Appointments</h3>
        {appointments.length > 0 ? (
          <ul className="appointments-list">
            {appointments.map((appointment) => (
              <li key={appointment.id} className="appointment-item">
                <p>
                  <strong>Patient:</strong> {appointment.patientName}
                </p>
                <p>
                  <strong>Date:</strong> {appointment.date}
                </p>
                <p>
                  <strong>Time:</strong> {appointment.time}
                </p>
                <p>
                  <strong>Status:</strong> {appointment.status}
                </p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No appointments available.</p>
        )}
      </div>

      <div className="chat-section">
        <h3>Chat with Patients</h3>
        <p>This is a placeholder for chat functionality.</p>
        <button onClick={() => alert('Chat feature coming soon!')}>
          Open Chat
        </button>
      </div>
    </div>
  );
};

export default Doctordashboard;
