// src/components/DoctorDashboard.jsx
import React, { useState } from 'react';
import './DoctorDashboard.css';

function Doctordashboard() {
  const [activeSection, setActiveSection] = useState('profile'); // Default section is 'profile'
  const [doctorProfile, setDoctorProfile] = useState({
    name: '',
    specialization: '',
    experience: '',
    profilePhoto: null,
  });
  const [availability, setAvailability] = useState([]);
  const [appointments, setAppointments] = useState([
    { patientName: 'John Doe', time: '10:00 AM', date: '2024-11-25' },
    { patientName: 'Jane Smith', time: '11:30 AM', date: '2024-11-25' },
  ]);

  // Handle file input for profile photo
  const handleFileChange = (e) => {
    setDoctorProfile({
      ...doctorProfile,
      profilePhoto: URL.createObjectURL(e.target.files[0]),
    });
  };

  // Save doctor profile
  const handleSaveProfile = (e) => {
    e.preventDefault();
    alert('Profile saved successfully!');
    console.log('Doctor Profile:', doctorProfile);
  };

  // Add availability
  const handleAddAvailability = (e) => {
    e.preventDefault();
    const newAvailability = {
      date: e.target.date.value,
      time: e.target.time.value,
    };
    setAvailability([...availability, newAvailability]);
    e.target.reset();
  };

  return (
    <div className="doctor-dashboard">
      <h1>Doctor Dashboard</h1>
      <div className="dashboard-navigation">
        <button onClick={() => setActiveSection('profile')}>Create Profile</button>
        <button onClick={() => setActiveSection('availability')}>Add Availability</button>
        <button onClick={() => setActiveSection('appointments')}>View Appointments</button>
      </div>

      <div className="dashboard-content">
        {activeSection === 'profile' && (
          <div className="profile-section">
            <h2>Create Profile</h2>
            <form onSubmit={handleSaveProfile}>
              <label>Name:</label>
              <input
                type="text"
                value={doctorProfile.name}
                onChange={(e) => setDoctorProfile({ ...doctorProfile, name: e.target.value })}
                required
              />
              <label>Specialization:</label>
              <input
                type="text"
                value={doctorProfile.specialization}
                onChange={(e) =>
                  setDoctorProfile({ ...doctorProfile, specialization: e.target.value })
                }
                required
              />
              <label>Experience (years):</label>
              <input
                type="number"
                value={doctorProfile.experience}
                onChange={(e) =>
                  setDoctorProfile({ ...doctorProfile, experience: e.target.value })
                }
                required
              />
              <label>Profile Photo:</label>
              <input type="file" onChange={handleFileChange} />
              {doctorProfile.profilePhoto && (
                <img
                  src={doctorProfile.profilePhoto}
                  alt="Profile"
                  className="profile-photo-preview"
                />
              )}
              <button type="submit">Save Profile</button>
            </form>
          </div>
        )}

        {activeSection === 'availability' && (
          <div className="availability-section">
            <h2>Add Availability</h2>
            <form onSubmit={handleAddAvailability}>
              <label>Date:</label>
              <input type="date" name="date" required />
              <label>Time:</label>
              <input type="time" name="time" required />
              <button type="submit">Add</button>
            </form>
            <h3>Availability List</h3>
            <ul>
              {availability.map((slot, index) => (
                <li key={index}>
                  {slot.date} at {slot.time}
                </li>
              ))}
            </ul>
          </div>
        )}

        {activeSection === 'appointments' && (
          <div className="appointments-section">
            <h2>Appointments</h2>
            <ul>
              {appointments.map((appointment, index) => (
                <li key={index}>
                  {appointment.patientName} - {appointment.date} at {appointment.time}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default Doctordashboard;
