import React, { useState } from "react";
import "./Doctordashboard.css"; // Import the CSS file

const Doctordashboard = () => {
  const [activeTab, setActiveTab] = useState("none");
  const [profileData, setProfileData] = useState(null);
  const [availability, setAvailability] = useState([]);
  const [appointments, setAppointments] = useState([
    { patient: "John Doe", date: "2024-12-01", time: "10:00 AM" },
    { patient: "Jane Smith", date: "2024-12-02", time: "11:30 AM" },
  ]);

  const [formData, setFormData] = useState({
    name: "",
    specialization: "",
    experience: "",
    profilePhoto: "",
  });

  const handleFormChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleProfileSubmit = (e) => {
    e.preventDefault();
    setProfileData(formData);
    setActiveTab("none");
    alert("Profile Created Successfully!");
  };

  const handleAddAvailability = (e) => {
    e.preventDefault();
    const { day, time } = e.target.elements;
    setAvailability([...availability, { day: day.value, time: time.value }]);
    e.target.reset();
  };

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      {/* Sidebar */}
      <div className="sidebar">
        <h2>Dashboard</h2>
        <button onClick={() => setActiveTab("createProfile")}>Create Profile</button>
        <button onClick={() => setActiveTab("availability")}>Add Availability</button>
        <button onClick={() => setActiveTab("appointments")}>View Appointments</button>
        <button onClick={() => setActiveTab("settings")}>Settings</button>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {activeTab === "createProfile" && (
          <div>
            <h2>Create Doctor Profile</h2>
            <form onSubmit={handleProfileSubmit}>
              <input
                type="text"
                name="name"
                placeholder="Name"
                value={formData.name}
                onChange={handleFormChange}
              />
              <input
                type="text"
                name="specialization"
                placeholder="Specialization"
                value={formData.specialization}
                onChange={handleFormChange}
              />
              <input
                type="text"
                name="experience"
                placeholder="Experience (in years)"
                value={formData.experience}
                onChange={handleFormChange}
              />
              <input
                type="file"
                name="profilePhoto"
                onChange={(e) =>
                  setFormData({ ...formData, profilePhoto: e.target.files[0] })
                }
              />
              <button type="submit">Submit</button>
            </form>
          </div>
        )}

        {activeTab === "availability" && (
          <div>
            <h2>Add Availability</h2>
            <form onSubmit={handleAddAvailability}>
              <input type="text" name="day" placeholder="Day (e.g., Monday)" />
              <input
                type="text"
                name="time"
                placeholder="Time Slot (e.g., 10:00 AM - 11:00 AM)"
              />
              <button type="submit">Add</button>
            </form>
            <ul>
              {availability.map((slot, index) => (
                <li key={index}>
                  {slot.day} - {slot.time}
                </li>
              ))}
            </ul>
          </div>
        )}

        {activeTab === "appointments" && (
          <div>
            <h2>View Appointments</h2>
            <ul>
              {appointments.map((appointment, index) => (
                <li key={index}>
                  {appointment.patient} - {appointment.date} - {appointment.time}
                </li>
              ))}
            </ul>
          </div>
        )}

        {activeTab === "settings" && (
          <div>
            <h2>Settings</h2>
            {profileData ? (
              <div>
                <p>
                  <strong>Name:</strong> {profileData.name}
                </p>
                <p>
                  <strong>Specialization:</strong> {profileData.specialization}
                </p>
                <p>
                  <strong>Experience:</strong> {profileData.experience} years
                </p>
                <div className="settings-buttons">
                  <button>Edit Profile</button>
                  <button>Delete Profile</button>
                </div>
              </div>
            ) : (
              <p>No profile data available. Please create a profile first.</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Doctordashboard;
