import React, { useState } from 'react';
//import DoctorList from './sections/DoctorList'; // Import Doctor List Component
//import CreateDoctor from './sections/CreateDoctor'; // Import Create Doctor Component
import './Dashboard.css'; // Import Dashboard styles

function Dashboard() {
  const [view, setView] = useState('doctorList'); // State to toggle between views
  const [doctors, setDoctors] = useState([]); // State to store the list of doctors

  // Function to add a new doctor
  const addDoctor = (doctor) => {
    setDoctors((prevDoctors) => [...prevDoctors, doctor]); // Add doctor to list
    setView('doctorList'); // Navigate back to doctor list view
  };

  return (
    <div className="dashboard">
      <h1>Welcome to the Dashboard</h1>

      {/* Navigation Bar */}
      <div className="dashboard-navigation">
        <button onClick={() => setView('doctorList')} className={view === 'doctorList' ? 'active' : ''}>
          Doctor List
        </button>
        <button onClick={() => setView('createDoctor')} className={view === 'createDoctor' ? 'active' : ''}>
          Add Doctor
        </button>
      </div>

      {/* Content Area */}
      <div className="dashboard-content">
        {view === 'doctorList' && <DoctorList doctors={doctors} />}
        {view === 'createDoctor' && <CreateDoctor addDoctor={addDoctor} />}
      </div>
    </div>
  );
}

export default Dashboard;
