import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import "./Doctordashboard.css"; // Add styling for the dashboard

const Doctordashboard = () => {
  const history = useHistory();

  // Integrated custom hook logic for managing profile
  const [profile, setProfile] = useState({
    name: "Dr. John Doe",
    specialization: "Cardiology",
    experience: "10 years",
    availability: "9:00 AM - 5:00 PM",
  });

  const updateProfile = (newData) => {
    setProfile((prev) => ({ ...prev, ...newData }));
  };

  const deleteProfile = () => {
    setProfile(null);
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>Doctor Panel</h2>
        <ul>
          <li onClick={() => history.push("/dashboard")}>Dashboard</li>
          <li onClick={() => history.push("/create-profile")}>Create Profile</li>
          <li onClick={() => history.push("/appointments")}>View Appointments</li>
          <li onClick={() => history.push("/availability")}>Availability</li>
          <li onClick={() => history.push("/settings")}>Settings</li>
          <li className="logout" onClick={() => history.push("/logout")}>
            Logout
          </li>
        </ul>
      </div>

      {/* Header Bar */}
      <div className="header">
        <h1>Welcome, {profile?.name || "Doctor"}</h1>
        <button onClick={() => history.push("/edit-profile")}>Edit Profile</button>
      </div>

      {/* Content Section */}
      <div className="content">
        {profile ? (
          <div className="profile-section">
            <h2>Profile Information</h2>
            <p><strong>Name:</strong> {profile.name}</p>
            <p><strong>Specialization:</strong> {profile.specialization}</p>
            <p><strong>Experience:</strong> {profile.experience}</p>
            <p><strong>Availability:</strong> {profile.availability}</p>
            <button onClick={() => updateProfile({ availability: "10:00 AM - 4:00 PM" })}>
              Edit Availability
            </button>
            <button className="delete-button" onClick={deleteProfile}>
              Delete Profile
            </button>
          </div>
        ) : (
          <p>No profile created yet. Please create your profile.</p>
        )}
      </div>
    </div>
  );
};

export default Doctordashboard;
