import React, { useState, useEffect } from "react";
import "./Doctordashboard.css";

const Doctordashboard = () => {
  const [activeTab, setActiveTab] = useState(""); // To toggle between tabs
  const [profileData, setProfileData] = useState(null); // Store doctor profile
  const [appointments, setAppointments] = useState([]); // Store appointments data
  const [profileFormData, setProfileFormData] = useState({
    name: "",
    DoctorID: "",
    specialization: "",
    experience: "",
    Location: "",
    Hospitalname: "",
    Email: "",
    MoblieNo: "",
    profilePhoto: null,
  });

  

  const handleLogout = () => {
    alert("Logged out successfully!");
    window.location.href = "/login"; // Redirect to login page
  };

  const handleProfileFormChange = (e) => {
    const { name, value } = e.target;
    setProfileFormData({ ...profileFormData, [name]: value });
  };

  const handleProfileSubmit = (e) => {
    e.preventDefault();
    setProfileData(profileFormData); // Save profile data
    setProfileFormData({
      name: "",
      DoctorID: "",
      specialization: "",
      experience: "",
      Location: "",
      Hospitalname: "",
      Email: "",
      MoblieNo: "",
      profilePhoto: null,
    });
    setActiveTab("doctorProfile"); // Show profile info
    alert("Profile created successfully!");
  };

  const handleEditProfile = () => {
    setProfileFormData(profileData); // Load existing profile data into the form
    setActiveTab("createProfile"); // Open the profile form for editing
  };

  const handleDeleteProfile = () => {
    if (window.confirm("Are you sure you want to delete your profile?")) {
      setProfileData(null);
      alert("Profile deleted successfully!");
    }
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <button onClick={() => setActiveTab("Dashboard")}>Dashboard</button>
        {profileData ? (
          <button onClick={() => setActiveTab("doctorProfile")}>Doctor Profile</button>
        ) : (
          <button onClick={() => setActiveTab("createProfile")}>Create Profile</button>
        )}
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <h1 className="dashboard-heading">Welcome to Doctor Dashboard</h1>

        {activeTab === "Dashboard" && (
          <div className="dashboard-info">
            <div className="info-box" style={{ backgroundColor: "#ffcccb" }}>
              <h3>Total Appointments</h3>
              <p>{appointments.length}</p>
            </div>
            <div className="info-box" style={{ backgroundColor: "#d4edda" }}>
              <h3>Completed Appointments</h3>
              <p>{appointments.filter((appt) => appt.status === "completed").length}</p>
            </div>
            <div className="info-box" style={{ backgroundColor: "#f8d7da" }}>
              <h3>Canceled Appointments</h3>
              <p>{appointments.filter((appt) => appt.status === "canceled").length}</p>
            </div>
            <div className="calendar-container">
              <h3>Calendar</h3>
              <iframe
                title="calendar"
                src="https://calendar.google.com/calendar/embed?src=your_calendar_id"
                style={{ width: "100%", height: "400px", border: "none" }}
              ></iframe>
            </div>
          </div>
        )}

        {activeTab === "createProfile" && (
          <div className="centered-form">
            <form onSubmit={handleProfileSubmit}>
              <h2>Create Doctor Profile</h2>
              <input
                type="text"
                name="name"
                placeholder="Name"
                value={profileFormData.name}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="DoctorID"
                placeholder="DoctorID"
                value={profileFormData.DoctorID}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="specialization"
                placeholder="Specialization"
                value={profileFormData.specialization}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="experience"
                placeholder="Experience"
                value={profileFormData.experience}
                onChange={handleProfileFormChange}
                required
              />
              <button type="submit">Submit</button>
            </form>
          </div>
        )}

        {activeTab === "doctorProfile" && profileData && (
          <div className="centered-form">
            <h2>Doctor Profile</h2>
            <p>Name: {profileData.name}</p>
            <button onClick={handleEditProfile}>Edit Profile</button>
            <button onClick={handleDeleteProfile} style={{ background: "red", color: "white" }}>
              Delete Profile
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Doctordashboard;
