import React, { useEffect, useState } from "react";
import "./PatientDashboard.css"; // Ensure you have this CSS file for the styles

const PatientDashboard = () => {
  const [activeTab, setActiveTab] = useState(""); // To toggle between tabs
  const [profileData, setProfileData] = useState(null); // Store patient profile
  const [appointmentHistory, setAppointmentHistory] = useState([]); // Store appointment history
  const [feedbackFormData, setFeedbackFormData] = useState({
    DoctorID: "",
    PatientID: "",
    Rating: 0,
    Feedback: "",
  });
  const [submittedFeedback, setSubmittedFeedback] = useState([]);

  const handleLogout = () => {
    alert("Logged out successfully!");
    window.location.href = "/login"; // Redirect to login page
  };

  const handleFeedbackFormChange = (e) => {
    const { name, value } = e.target;
    setFeedbackFormData({ ...feedbackFormData, [name]: value });
  };

  const handleRatingChange = (rating) => {
    setFeedbackFormData({ ...feedbackFormData, Rating: rating });
  };

  const handleFeedbackSubmit = (e) => {
    e.preventDefault();
    setSubmittedFeedback([...submittedFeedback, feedbackFormData]); // Save feedback
    setFeedbackFormData({ DoctorID: "", PatientID: "", Rating: 0, Feedback: "" }); // Reset form
    alert("Feedback submitted successfully!");
    setActiveTab(""); // Close the feedback form
  };

  return (
    <div className="patient-dashboard-container">
      {/* Sidebar */}
      <div className="patient-sidebar">
        <div className="sidebar-header">
          <h2 style={{ color: "#fff", fontWeight: "bold", fontSize: "24px" }}>
            PATIENT DASHBOARD
          </h2>
        </div>
        <div className="sidebar-links">
          <button
            onClick={() =>
              setActiveTab(profileData ? "patientProfile" : "createProfile")
            }
            className="sidebar-button"
          >
            <i className="fas fa-user"></i> {profileData ? "View Profile" : "Create Profile"}
          </button>
          <button
            onClick={() => setActiveTab("bookAppointment")}
            className="sidebar-button"
          >
            <i className="fas fa-calendar-plus"></i> Book Appointment
          </button>
          <button
            onClick={() => setActiveTab("appointmentHistory")}
            className="sidebar-button"
          >
            <i className="fas fa-history"></i> Appointment History
          </button>
          <button
            onClick={() => setActiveTab("feedback")}
            className="sidebar-button"
          >
            <i className="fas fa-comment-alt"></i> Feedback
          </button>
        </div>
        <button className="logout-button" onClick={handleLogout}>
          <i className="fas fa-sign-out-alt"></i> Logout
        </button>
      </div>

      {/* Main Content */}
      <div className="patient-main-content">
        {/* Feedback Form */}
        {activeTab === "feedback" && (
          <div className="feedback-form-container">
            <div className="card feedback-form">
              <h2>Provide Feedback</h2>
              <form onSubmit={handleFeedbackSubmit}>
                <input
                  type="text"
                  name="DoctorID"
                  placeholder="Doctor ID"
                  value={feedbackFormData.DoctorID}
                  onChange={handleFeedbackFormChange}
                  required
                />
                <input
                  type="text"
                  name="PatientID"
                  placeholder="Patient ID"
                  value={feedbackFormData.PatientID}
                  onChange={handleFeedbackFormChange}
                  required
                />
                <div className="rating-container">
                  <p>Rating:</p>
                  {[1, 2, 3, 4, 5].map((star) => (
                    <span
                      key={star}
                      className={`star ${
                        feedbackFormData.Rating >= star ? "filled" : ""
                      }`}
                      onClick={() => handleRatingChange(star)}
                    >
                      ★
                    </span>
                  ))}
                </div>
                <textarea
                  name="Feedback"
                  placeholder="Write your feedback here..."
                  value={feedbackFormData.Feedback}
                  onChange={handleFeedbackFormChange}
                  required
                ></textarea>
                <button type="submit">Submit Feedback</button>
              </form>
            </div>
          </div>
        )}

        {/* Submitted Feedback */}
        {submittedFeedback.length > 0 && (
          <div className="card feedback-history">
            <h2>Submitted Feedback</h2>
            <ul>
              {submittedFeedback.map((feedback, index) => (
                <li key={index}>
                  <strong>Doctor ID:</strong> {feedback.DoctorID} <br />
                  <strong>Patient ID:</strong> {feedback.PatientID} <br />
                  <strong>Rating:</strong> {feedback.Rating} ★ <br />
                  <strong>Feedback:</strong> {feedback.Feedback}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default PatientDashboard;
