// src/components/CreateDoctor.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function CreateDoctor() {
  const [doctor, setDoctor] = useState({
    name: '',
    id: '',
    speciality: '',
    location: '',
    hospital: '',
    email: '',
    phone: '',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setDoctor((prevDoctor) => ({
      ...prevDoctor,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add the doctor to your backend or a local state (in real scenario, use an API call)
    // Then navigate back to the DoctorList page
    navigate('/doctorlist');
  };

  return (
    <div className="create-doctor">
      <h2>Add Doctor</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Doctor Name:
          <input type="text" name="name" value={doctor.name} onChange={handleChange} required />
        </label>
        <label>
          Doctor ID:
          <input type="text" name="id" value={doctor.id} onChange={handleChange} required />
        </label>
        <label>
          Speciality:
          <input type="text" name="speciality" value={doctor.speciality} onChange={handleChange} required />
        </label>
        <label>
          Location:
          <input type="text" name="location" value={doctor.location} onChange={handleChange} required />
        </label>
        <label>
          Hospital Name:
          <input type="text" name="hospital" value={doctor.hospital} onChange={handleChange} required />
        </label>
        <label>
          Email:
          <input type="email" name="email" value={doctor.email} onChange={handleChange} required />
        </label>
        <label>
          Phone Number:
          <input type="tel" name="phone" value={doctor.phone} onChange={handleChange} required />
        </label>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default CreateDoctor;
