import React, { useState } from "react";
import "./PatientDashboard.css"; // Ensure you have this CSS file for the styles

const PatientDashboard = () => {
  const [activeTab, setActiveTab] = useState(""); // To toggle between tabs
  const [showFeedbackForm, setShowFeedbackForm] = useState(false); // To toggle feedback form visibility
  const [feedbackFormData, setFeedbackFormData] = useState({
    PatientID: "",
    DoctorID: "",
    Rating: "",
  });

  const handleFeedbackFormChange = (e) => {
    const { name, value } = e.target;
    setFeedbackFormData({ ...feedbackFormData, [name]: value });
  };

  const handleFeedbackSubmit = (e) => {
    e.preventDefault();
    console.log("Feedback Submitted:", feedbackFormData);
    alert("Thank you for your feedback!");
    setFeedbackFormData({
      PatientID: "",
      DoctorID: "",
      Rating: "",
    });
    setShowFeedbackForm(false); // Close feedback form after submission
  };

  return (
    <div className="patient-dashboard-container">
      {/* Sidebar */}
      <div className="patient-sidebar">
        <div className="sidebar-header">
          <h2 style={{ color: "#fff", fontWeight: "bold", fontSize: "24px" }}>
            PATIENT DASHBOARD
          </h2>
        </div>
        <div className="sidebar-links">
          {/* Other buttons */}
          <button
            onClick={() => setShowFeedbackForm(true)}
            className="sidebar-button"
          >
            <i className="fas fa-comment-dots"></i> Give Feedback
          </button>
        </div>
      </div>

      {/* Feedback Form (Modal) */}
      {showFeedbackForm && (
        <div className="feedback-modal">
          <div className="feedback-form-container">
            <h2>Feedback Form</h2>
            <form onSubmit={handleFeedbackSubmit}>
              <input
                type="text"
                name="PatientID"
                placeholder="Patient ID"
                value={feedbackFormData.PatientID}
                onChange={handleFeedbackFormChange}
                required
              />
              <input
                type="text"
                name="DoctorID"
                placeholder="Doctor ID"
                value={feedbackFormData.DoctorID}
                onChange={handleFeedbackFormChange}
                required
              />
              <select
                name="Rating"
                value={feedbackFormData.Rating}
                onChange={handleFeedbackFormChange}
                required
              >
                <option value="">Select Rating</option>
                <option value="1">1 - Poor</option>
                <option value="2">2 - Fair</option>
                <option value="3">3 - Good</option>
                <option value="4">4 - Very Good</option>
                <option value="5">5 - Excellent</option>
              </select>
              <button type="submit">Submit Feedback</button>
            </form>
            <button
              className="close-feedback-button"
              onClick={() => setShowFeedbackForm(false)}
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* Main Content */}
      <div className="patient-main-content">
        <header className="patient-dashboard-header">
          <h1>Your Personalized Health Hub!</h1>
        </header>
        <h3>The best doctors for your care</h3>
      </div>
    </div>
  );
};

export default PatientDashboard;
