import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
const [error, setError] = useState(''); 
import './Login.css';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState(''); // Email state to store the email entered for password reset
  const [showForgotPassword, setShowForgotPassword] = useState(false); // State to show/hide forgot password form
  const navigate = useNavigate();

  // Hardcoded admin credentials
  const adminCredentials = {
    username: 'admin', // Admin username
    password: 'admin123', // Admin password
  };

  // Handle forgot password logic
  const handleForgotPassword = () => {
    if (email) {
      // Simulating sending a reset email
      alert(`Password reset link has been sent to ${email}`);
      setShowForgotPassword(false); // Close the forgot password form
    } else {
      alert('Please enter your email address.');
    }
  };

  const handleLogin = (e) => {
    e.preventDefault();
  
    // Retrieve users from localStorage
    const users = JSON.parse(localStorage.getItem('users')) || [];
  
    // Check if the user exists with matching credentials
    const user = users.find(user => user.email === email && user.password === password);
  
    if (user) {
      // Save user details in localStorage or sessionStorage to keep the user logged in
      localStorage.setItem('currentUser', JSON.stringify(user));
      navigate('/dashboard'); // Redirect to dashboard after successful login
    } else {
      setError('Invalid email or password');
    }
  };
  
  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>Login</h2>
        <label htmlFor="username">Username</label>
        <input
          type="text"
          id="username"
          name="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
        <p>New user? <a href="/register">Register here</a></p>

        {/* Forgot Password button */}
        <button
          type="button"
          onClick={() => setShowForgotPassword(true)} // Trigger to show forgot password form
          className="forgot-password-button"
        >
          Forgot Password?
        </button>
      </form>

      {/* Forgot Password Form */}
      {showForgotPassword && (
        <div className="forgot-password-form">
          <h3>Enter your email to reset password</h3>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)} // Update the email state on change
            required
          />
          <button type="button" onClick={handleForgotPassword}>
            Send Reset Link
          </button>
          <button type="button" onClick={() => setShowForgotPassword(false)}>
            Cancel
          </button>
        </div>
      )}
    </div>
  );
}

export default Login;
